SELECT T1.SRECS_SRNG_CLM_SE_SQNO /* 심사재심의심사청구구분순번 */,
    T1.NTFCTN_RCPT_YMD /* 통보접수일자 */,
    F_ORG_INFO(T1.INST_CD, '1') AS PROF_RBPRSN_INST_NM /* 증명책임자기관명 */,
    T1.RSCH_ASMT_TTL /* 연구과제제목 */,
    T5.ENFC_YMD /* 시행일자 */,
    F_CODE_NM('1000002', T4.DSPS_RQT_KDCD) AS DSPS_RQT_KND_CVN /* 처분요구종류코드값명 */,
    TO_CHAR(T5.PNRT_PRSRV_AMT, '999,999,999,999') AS PNRT_PRSRV_AMT /* 추징회수보전금액 */,
    T5.NOP_CONT /* 인원개수 */,
    T5.PIC_NM /* 담당자명 */,
    T1.PRCS_STTS_CN /* 처리상태내용 */,
    T2.SRECS_GNLGR_ATRZ_YMD /* 심사재심의원장결재일자 */,
    T1.SRECS_PRCS_PIC_NM /* 심사재심의처리담당자명 */,
    T2.SRECS_CMT_LDRV_MBCMT_NM /* 심사재심의위원회주심위원위원명 */,
    T2.SRECS_SBCN_YMD /* 심사재심의부의일자 */,
    F_CODE_NM('1000045', T2.SRECS_DCS_KDCD) AS SRECS_DCS_KND_CN /* 심사재심의의결종류내용 */,
    T1.SRECS_REL_AMT /* 심사재심의관계금액 */,
    T1.SRECS_RLPR_NOPE /* 심사재심의관계자인원수 */,
    T3.SRECS_ENFC_YMD /* 심사재심의시행일자 */,
    T1.SRECS_OW_PRCS_RMRK_CN /* 심사재심의사무처리비고내용 */,
    T1.CVLCPT_RCPT_YR /* 민원접수년도 */,
    T1.SRECS_RCPT_SECD /* 심사재심의접수구분코드 */,
    T1.SRECS_RCPT_SQNO /* 심사재심의접수순번 */,
    T1.SRECS_DEPT_CD /* 심사재심의부서코드 */,
    T1.SRECS_STATS_CONT /* 심사재심의통계개수 */,
    F_DPT_INFO(T1.SRECS_DEPT_CD, '1') AS CNSTN_MBCMT_DEPT_NM /* 자문위원부서명 */,
    (SELECT TO_CHAR(TO_DATE(X1.TRF_DT),'YYYY-MM-DD') || '(' || F_DPT_INFO(X1.TRF_BEF_CHR_DPT_CD, '1') || '->' || F_DPT_INFO(T1.SRECS_DEPT_CD, '1') || ')' AS SRECS_DEPT_CD /* 심사재심의부서코드 */
FROM TBBADHAB002H X1 WHERE X1.CVLCPT_TASK_SECD = '06' AND X1.REF_YR = T1.CVLCPT_RCPT_YR AND X1.REF_SRNO = T1.SRECS_RCPT_SQNO AND X1.CMPTN_YN = 'Y' ) AS TRF_BEF_AF_DPT /* 이관처리 전 후 부서 */ , T1.CVLCPT_RCPT_YR || '-' || DECODE(T1.SRECS_RCPT_SECD,'1','심사','2','재심','3','재심판정') || '-' || T1.SRECS_RCPT_SQNO AS NTFCTN_RCPT_SQNO /*접수번호-2015.12.08 yyh*/ FROM TB_BADFRE012M T1 , TB_BADFRE012D T2 , TB_BADFRE013D T3 , TB_BADEAR001M T4 , TB_BADEAR002D T5 WHERE T1.CVLCPT_RCPT_YR = T2.SRECS_RCPT_YR(+) AND T1.SRECS_RCPT_SECD = T2.SRECS_RCPT_SECD(+) AND T1.SRECS_RCPT_SQNO = T2.SRECS_RCPT_SQNO(+) AND T1.SRECS_DEPT_CD = T2.SRECS_DEPT_CD(+) AND T1.CVLCPT_RCPT_YR = T3.SRECS_RCPT_YR(+) AND T1.SRECS_RCPT_SECD = T3.SRECS_RCPT_SECD(+) AND T1.SRECS_RCPT_SQNO = T3.SRECS_RCPT_SQNO(+) AND T1.SRECS_DEPT_CD = T3.SRECS_DEPT_CD(+) AND T1.CVLCPT_ADT_YR = T4.ADT_YR(+) AND T1.ADT_NO = T4.ADT_NO(+) AND T1.DSPS_RQT_SQNO = T4.DSPS_RQT_SQNO(+) AND T1.CVLCPT_ADT_YR = T5.ADT_YR(+) AND T1.ADT_NO = T5.ADT_NO(+) AND T1.DSPS_RQT_SQNO = T5.DSPS_RQT_SQNO(+) AND T1.SRECS_SRNG_CLM_SECD IN ('41','42','43','49') AND T1.SRECS_DEPT_CD IN (SELECT SRECS_DEPT_CD /* 심사재심의부서코드 */
FROM TABLE(F_AUTH_DEPT(#strCnb#, #strDptAuth#, '', #strDptCd#))) AND T1.CVLCPT_RCPT_YR = #CVLCPT_RCPT_YR# AND T1.SRECS_RCPT_SECD = #SRECS_RCPT_SECD# AND T1.SRECS_RCPT_SQNO = #SRECS_RCPT_SQNO# AND T1.SRECS_DEPT_CD = #SRECS_DEPT_CD# AND T1.CVLCPT_RCPT_YR = #CVLCPT_RCPT_YR# AND T1.SRECS_DEPT_CD = #SRECS_DEPT_CD# AND T1.SRECS_RCPT_SQNO = #SRECS_RCPT_SQNO# ORDER BY T1.CVLCPT_RCPT_YR DESC, T1.SRECS_RCPT_SECD DESC, T1.SRECS_DEPT_CD DESC, T1.SRECS_SRNG_CLM_SE_SQNO DESC