SELECT T1.INST_CD /* 기관코드 */,
    F_ORG_INFO(T1.INST_CD, '1') || chr(10) || (SELECT CASE WHEN COUNT(X1.FRS_MDM_SQNO) = 1 THEN MAX(X1.RLPR_NM) WHEN COUNT(X1.FRS_MDM_SQNO) > 1 THEN MAX(X1.RLPR_NM) || '외 ' || ( COUNT(X1.FRS_MDM_SQNO) - 1 ) || '명' ELSE '' AS F_ORG_INFO
FROM TB_BADGDA001L X1 WHERE 1=1 AND X1.NTFCTN_INST_CD = T1.INST_CD AND X1.NTFCTN_SECD = T1.NTFCTN_SECD AND X1.NTFCTN_SQNO = T1.NTFCTN_SQNO GROUP BY INST_CD, NTFCTN_SECD, NTFCTN_SQNO ) AS RLTN_ORG_DFNT_NMS /*관계기관 관계자명*/ , T1.NTFCTN_SECD /*통보구분코드*/ ,(SELECT TO_CHAR(TO_DATE(X1.TRF_DT),'YYYY-MM-DD') || '(' || F_DPT_INFO(X1.TRF_BEF_CHR_DPT_CD, '1') || '->' || F_DPT_INFO(T1.SRECS_DEPT_CD, '1') || ')' AS SRECS_DEPT_CD /* 심사재심의부서코드 */
FROM TBBADHAB002H X1 WHERE X1.CMPTN_YN = 'Y' AND X1.CVLCPT_TASK_SECD = '05' AND X1.REF_DVSN_CD = T1.NTFCTN_SECD AND X1.REF_SRNO = T1.NTFCTN_SQNO AND X1.RFRNC_INST_CD = T1.INST_CD ) AS TRF_BEF_AF_DPT , T1.LINK_YR /*년도*/ , T1.SRECS_DEPT_CD /*부서코드(x)*/ , F_DPT_INFO(T1.SRECS_DEPT_CD, '1') AS CNSTN_MBCMT_DEPT_NM /*부서코드*/ , T1.NTFCTN_SQNO /*통보번호*/ , T1.OCRN_SECD /*발생구분코드*/ , F_CODE_NM('1000202', T1.OCRN_SECD) AS OGN_DVSN , T1.VIOL_SECD , DECODE(T1.NTFCTN_SECD, '2', F_CODE_NM('1000201', T1.VIOL_SECD), '') AS WRDO2 , DECODE(T1.NTFCTN_SECD, '1', F_CODE_NM('1000241', T1.VIOL_SECD), '') AS WRDO1 , T1.STT_RCPT_STTS_SECD /*접수상태코드*/ , T1.NTFCTN_RCPT_YMD /*접수일자*/ , T1.BAI_DEPT_CD /*감사원국과코드(o)*/ , T1.SRECS_RLPR_NOPE /*관계자명(수)*/ , T1.HRM_AMT /*손해금액*/ , T1.SELF_CPS_AMT /*자체변상금액*/ , DECODE(T1.NTFCTN_SECD, '2', T1.HRM_AMT, '1', T1.SELF_CPS_AMT ) AS DSPS_RQT_AMT , TRIM(DECODE(T1.NTFCTN_SECD, '2', TO_CHAR(T1.HRM_AMT,'999,999,999,999'), '1', TO_CHAR(T1.SELF_CPS_AMT,'999,999,999,999') )) || '천원 ' || '(' || T1.SRECS_RLPR_NOPE || '명)' AS AM_TXT , T1.PRCS_STTS_CN /*처리상황내용*/ , T1.PRCS_YMD /*처리일자*/ , T1.SELF_ATRZ_YMD /*결재일자*/ , T1.CMT_SBCN_CN /*위원회부의내용*/ , T1.ENFC_YMD /*시행일자*/ , T1.ADT_DATA_RMRK_CN /*감사자료비교*/ , T1.RSCH_ASMT_TTL /*제목명*/ ,(SELECT PROF_RBPRSN_INST_NM /* 증명책임자기관명 */
FROM TBDCMACM015M WHERE INST_CD = T1.INST_CD) || '-' || (SELECT CD_NM
FROM TBDCMACM013C WHERE CMM_CD_DVSN_CD ='1000202' AND CD = T1.OCRN_SECD AND NVL(USE_YN,'N') ='Y') || '-' || T1.NTFCTN_SQNO AS NTF_SBJ_NO /* 통보사항번호 */ ,CASE WHEN (T1.NTFCTN_RCPT_SQNO IS NOT NULL AND T1.NTFCTN_SECD = '2') THEN LINK_YR || '-' || DECODE(T1.OCRN_SECD,'6','판청','손망') || '-' || T1.NTFCTN_RCPT_SQNO ELSE '' END AS ACP_NO_TXT /* 접수번호 */ FROM TB_BADGDA001M T1 WHERE 1=1 /* AND T1.NTF_DVSN_CD = '2' */ /* 통보구분코드 (1:범죄및징계, 2:망실및훼손) */ AND T1.STT_RCPT_STTS_SECD IN ('71', '72', '75', '76', '77') /*접수상태코드*/ AND T1.SRECS_DEPT_CD IN (SELECT SRECS_DEPT_CD /* 심사재심의부서코드 */
FROM TABLE(F_AUTH_DEPT(#strCnb#, #strDptAuth#, '', #strDptCd#))) AND T1.SRECS_DEPT_CD = #SRECS_DEPT_CD# AND T1.NTFCTN_RCPT_YMD BETWEEN #strYr# || '0101' AND #strYr# || '1231' AND T1.NTFCTN_RCPT_YMD BETWEEN #REF_YR# || '0101' AND #REF_YR# || '1231' AND T1.INST_CD = #INST_CD# AND T1.NTFCTN_SQNO = #NTFCTN_SQNO# AND T1.SRECS_DEPT_CD = #SRECS_DEPT_CD# AND T1.INST_CD = #INST_CD# AND T1.NTFCTN_SECD = #NTFCTN_SECD# AND T1.NTFCTN_SQNO = #NTFCTN_SQNO# AND T1.NTFCTN_RCPT_YMD BETWEEN #strAcpDtStart# AND #strAcpDtEnd# --> ORDER BY T1.ACP_DT DESC ,T1.ORG_CD ,T1.NTF_DVSN_CD ,T1.NNB DESC