SELECT T1.NTFCTN_RCPT_YMD /* 통보접수일자 */,
    F_ORG_INFO(T1.INST_CD, '1') AS PROF_RBPRSN_INST_NM /* 증명책임자기관명 */,
    T1.RSCH_ASMT_TTL /* 연구과제제목 */,
    T1.OTSD_EXPRT_ADDR || T1.CRU_CVLPR_DADDR AS OTSD_EXPRT_ADDR /* 외부전문가주소 */,
    T1.SRECS_CLMT_NM /* 심사재심의청구인명 */,
    T1.SRECS_PRCS_PIC_NM /* 심사재심의처리담당자명 */,
    T1.PRCS_STTS_CN /* 처리상태내용 */,
    F_DPT_INFO(T3.ENFC_DEPT_CD, '1') AS F_DPT_INFO /* 원처분요구국과 */,
    T3.PIC_NM /* 담당자명 */,
    T2.SRECS_GNLGR_ATRZ_YMD /* 심사재심의원장결재일자 */,
    T2.SRECS_CMT_LDRV_MBCMT_NM /* 심사재심의위원회주심위원위원명 */,
    T2.SRECS_SBCN_YMD /* 심사재심의부의일자 */,
    F_CODE_NM('1000045', T2.SRECS_DCS_KDCD) AS SRECS_DCS_KND_CN /* 심사재심의의결종류내용 */,
    T3.ENFC_YMD /* 시행일자 */,
    T1.SRECS_OW_PRCS_RMRK_CN /* 심사재심의사무처리비고내용 */,
    T1.CVLCPT_RCPT_YR /* 민원접수년도 */,
    T1.SRECS_RCPT_SECD /* 심사재심의접수구분코드 */,
    T1.SRECS_RCPT_SQNO /* 심사재심의접수순번 */,
    T1.SRECS_DEPT_CD /* 심사재심의부서코드 */,
    T1.SRECS_STATS_CONT /* 심사재심의통계개수 */,
    F_DPT_INFO(T1.SRECS_DEPT_CD, '1') AS CNSTN_MBCMT_DEPT_NM /* 자문위원부서명 */,
    (SELECT TO_CHAR(TO_DATE(X1.TRF_DT),'YYYY-MM-DD') || '(' || F_DPT_INFO (X1.TRF_BEF_CHR_DPT_CD, '1') || '->' || F_DPT_INFO (T1.SRECS_DEPT_CD, '1') || ')' AS SRECS_DEPT_CD /* 심사재심의부서코드 */
FROM TBBADHAB002H X1 WHERE X1.CMPTN_YN = 'Y' AND X1.CVLCPT_TASK_SECD = '07' AND X1.REF_YR = T1.CVLCPT_RCPT_YR AND X1.REF_SRNO = T1.SRECS_RCPT_SQNO) AS TRF_BEF_AF_DPT /*이관부서*/ , T1.SRECS_SRNG_CLM_SE_SQNO /* 심사청구구분일련번호 */ , T1.SRECS_SRNG_CLM_SECD /* 심사청구구분코드 */ , CASE WHEN SUBSTR(T1.SRECS_SRNG_CLM_SECD,0,1) = '1' THEN '국세' WHEN SUBSTR(T1.SRECS_SRNG_CLM_SECD,0,1) = '2' THEN '지방세' WHEN SUBSTR(T1.SRECS_SRNG_CLM_SECD,0,1) = '3' THEN '조세외' WHEN SUBSTR(T1.SRECS_SRNG_CLM_SECD,0,1) = '5' THEN '관세' END || '-' || T1.SRECS_SRNG_CLM_SE_SQNO AS JDG_REQ_DVSN_SRNO_TXT , T1.CVLCPT_RCPT_YR || '-' || DECODE(T1.SRECS_RCPT_SECD,'1','심사','2','재심','3','재심판정') || '-' || T1.SRECS_RCPT_SQNO AS NTFCTN_RCPT_SQNO /*접수번호 - 20151209 yyh*/ FROM TB_BADFRE012M T1 , TB_BADFRE012D T2 , TB_BADEAR002D T3 WHERE T1.CVLCPT_RCPT_YR = T2.SRECS_RCPT_YR(+) AND T1.SRECS_RCPT_SECD = T2.SRECS_RCPT_SECD(+) AND T1.SRECS_RCPT_SQNO = T2.SRECS_RCPT_SQNO(+) AND T1.SRECS_DEPT_CD = T2.SRECS_DEPT_CD(+) AND T1.CVLCPT_ADT_YR = T3.ADT_YR(+) AND T1.ADT_NO = T3.ADT_NO(+) AND T1.DSPS_RQT_SQNO = T3.DSPS_RQT_SQNO(+) AND T1.SRECS_SRNG_CLM_SECD NOT IN ('41','42','43','49') AND T1.SRECS_DEPT_CD IN (SELECT SRECS_DEPT_CD /* 심사재심의부서코드 */
FROM TABLE(F_AUTH_DEPT(#strCnb#, #strDptAuth#, '', #strDptCd#))) AND T1.CVLCPT_RCPT_YR = #CVLCPT_RCPT_YR# AND T1.SRECS_RCPT_SECD = #SRECS_RCPT_SECD# AND T1.SRECS_RCPT_SQNO = #SRECS_RCPT_SQNO# AND T1.SRECS_DEPT_CD = #SRECS_DEPT_CD# AND T1.SRECS_SRNG_CLM_SECD LIKE SUBSTR(#strJdgReqDvsnCd#,0,1) || '%' /*심사청구구분코드*/ AND T1.CVLCPT_RCPT_YR = #CVLCPT_RCPT_YR# AND T1.SRECS_DEPT_CD = #SRECS_DEPT_CD# AND T1.SRECS_RCPT_SQNO = #SRECS_RCPT_SQNO# ORDER BY T1.CVLCPT_RCPT_YR DESC, T1.SRECS_RCPT_SECD DESC, T1.SRECS_DEPT_CD DESC, SUBSTR(T1.SRECS_SRNG_CLM_SECD, 0, 1) DESC, T1.SRECS_SRNG_CLM_SE_SQNO DESC