SELECT T1.LINK_YR /* 연계년도 */,
    T1.SRECS_DEPT_CD /* 심사재심의부서코드 */,
    (SELECT COUNT(*) || ' 건'
FROM TB_BADJFS002D T3 WHERE T1.LINK_YR = T3.FNNC_INFO_SBMSN_YR AND T1.SRECS_DEPT_CD = T3.FNNC_INFO_SBMSN_RQT_DEPT_CD AND T1.SPCL_MNG_IMPL_SQNO = T3.FNNC_INFO_SBMSN_RQT_SQNO ) AS DTL_CNT , F_DPT_INFO(T1.SRECS_DEPT_CD, '1') AS CNSTN_MBCMT_DEPT_NM /*부서명*/ , T1.ADT_NO /*감사번호*/ , T1.CVLCPT_ADT_YR /*감사연도*/ , T2.ADT_MTTR_NM /*감사사항명*/ , T1.SPCL_MNG_IMPL_SQNO /*연번*/ , T1.FNNC_INFO_SBMSN_RQT_DOC_NO /*문서번호*/ , T1.TRGT_INST_CD /*대상기관코드*/ , F_ORG_INFO(T1.TRGT_INST_CD, '1') AS TRGT_INST_NM /*대상기관명*/ , T1.PUBOL_SRCH_PTOT_NOCS /*조회건수순계공직*/ , T1.GNRL_INDV_SRCH_PTOT_NOCS /*조회건수순계일반*/ , T1.ACTN_CN /*조치내용*/ , T1.RMRK_CN /*비고*/ , T1.ONR_SNDNG_YMD /*온나라발송일*/ , T1.ONR_SNDNG_PIC_ID /*온나라발송자ID*/ , T1.FRST_USER_ID /*최초사용자ID*/ , T1.LAST_USER_ID /*최종사용자ID*/ , T1.LAST_PRCS_DEPT_CD /*최종거래부서코드*/ , T1.LAST_MENU_ID /*최종메뉴ID*/ , T1.FRST_REG_DT /*최초등록일시*/ , T1.LAST_MDFCN_DT /*최종수정일시*/ , (SELECT TO_CHAR(TO_DATE(X1.TRF_DT),'YYYY-MM-DD') || '(' || F_DPT_INFO(X1.TRF_BEF_CHR_DPT_CD, '1') || '->' || F_DPT_INFO(T1.SRECS_DEPT_CD, '1') || ')' AS SRECS_DEPT_CD /* 심사재심의부서코드 */
FROM TBBADHAB002H X1 WHERE X1.CVLCPT_TASK_SECD = '14' AND X1.REF_YR = T1.LINK_YR AND X1.REF_SRNO = T1.SPCL_MNG_IMPL_SQNO AND X1.TRF_AF_CHR_DPT_CD = T1.SRECS_DEPT_CD AND X1.CMPTN_YN = 'Y' ) AS TRF_BEF_AF_DPT /* 이관처리 전 후 부서 */ FROM TB_BADJFS001M T1 , TB_BADDED001M T2 WHERE 1=1 AND T1.CVLCPT_ADT_YR(+) = T2.ADT_YR AND T1.ADT_NO(+) = T2.ADT_NO AND T1.SRECS_DEPT_CD IN (SELECT SRECS_DEPT_CD /* 심사재심의부서코드 */
FROM TABLE(F_AUTH_DEPT(#strCnb#, #strDptAuth#, '', #strDptCd#))) AND T1.LINK_YR = #LINK_YR# AND T1.SRECS_DEPT_CD = #SRECS_DEPT_CD# AND T1.SPCL_MNG_IMPL_SQNO = #SPCL_MNG_IMPL_SQNO# AND T1.CVLCPT_ADT_YR = #CVLCPT_ADT_YR# AND T1.ADT_NO = #ADT_NO# AND T1.SRECS_DEPT_CD = #SRECS_DEPT_CD# ORDER BY T1.LINK_YR DESC, T1.SRECS_DEPT_CD ASC, T1.SPCL_MNG_IMPL_SQNO DESC