SELECT T1.INST_CD /* 기관코드 */,
    T1.LINK_YR /* 연계년도 */,
    T1.FRS_MDM_SQNO /* 포렌식매체순번 */,
    T1.SRECS_DEPT_CD /* 심사재심의부서코드 */,
    F_DPT_INFO(T1.SRECS_DEPT_CD, '1') AS CNSTN_MBCMT_DEPT_NM /* 자문위원부서명 */,
    (SELECT MAX(TO_CHAR(TO_DATE(X1.TRF_DT),'YYYY-MM-DD') || '(' || F_DPT_INFO(X1.TRF_BEF_CHR_DPT_CD, '1') || '->' || F_DPT_INFO(T1.SRECS_DEPT_CD, '1') || ')' ) AS SRECS_DEPT_CD /* 심사재심의부서코드 */
FROM TBBADHAB002H X1 WHERE X1.CMPTN_YN = 'Y' AND X1.CVLCPT_TASK_SECD = '04' AND X1.RFRNC_INST_CD = T1.INST_CD AND X1.REF_YR = T1.LINK_YR AND X1.REF_SRNO = T1.FRS_MDM_SQNO ) AS TRF_BEF_AF_DPT , T1.NTFCTN_RCPT_YMD /*접수일자*/ , '교환' AS CONT_WAY /*계약종류*/ , '재산' AS CONT_TXT /*계약내용*/ , F_ORG_INFO(T1.INST_CD, '1') AS PROF_RBPRSN_INST_NM /*대상기관*/ , T1.NTFCTN_NM /*제목(계약명)*/ , '' AS APRS_EVL_AMT/*감정평가액*/ , '' AS DSPS_ACQS_SECD/*처분취득구분코드*/ , '' AS DSP_ACQ_DVSN , T1.PRCS_STTS_CN /*처리상황내용*/ , TO_CHAR(T1.CFMTN_DT,'YYYYMMDD') AS DTM_DT /*확정(처리)일자*/ , TO_CHAR(T3.APV_DH,'YYYYMMDD') AS APV_DH/*결재일자*/ , T1.ATRZ_DOC_NO /*결재참조번호*/ , T1.CMT_SBCN_CN /*위원회의*/ , T1.ENFC_YMD /*시행일자*/ , T1.ADT_DATA_RMRK_CN /*비고*/ FROM TB_BADGDA004M T1 /* 관련법령통지통보 */ , (SELECT MAX(S2.APV_DH) AS APV_DH,
    MAX(S2.APV_SNO) AS APV_SNO,
    MAX(S1.ATRZ_DOC_NO) AS ATRZ_DOC_NO /* 결재문서번호 */
FROM TBDCMACM022L S1 , TBDCMACM023L S2 WHERE S1.ATRZ_DOC_NO = S2.ATRZ_DOC_NO AND S1.TPOF_ATRZ_SECD = S2.TPOF_ATRZ_SECD AND S2.TPOF_ATRZ_SECD = 'NatzFortExc' GROUP BY S2.ATRZ_DOC_NO, S2.TPOF_ATRZ_SECD ) T3 WHERE 1=1 AND T1.STT_NTFCTN_SECD = '200' AND T1.ATRZ_DOC_NO = T3.ATRZ_DOC_NO(+) AND T1.STT_RCPT_STTS_SECD IN ('71', '72', '75', '76', '77') /*접수상태코드*/ AND T1.SRECS_DEPT_CD IN (SELECT SRECS_DEPT_CD /* 심사재심의부서코드 */
FROM TABLE(F_AUTH_DEPT(#strCnb#, #strDptAuth#, '', #strDptCd#))) AND T1.SRECS_DEPT_CD = #SRECS_DEPT_CD# AND T1.NTFCTN_RCPT_YMD BETWEEN #strYr# || '0101' AND #strYr# || '1231' AND T1.INST_CD = #INST_CD# AND T1.FRS_MDM_SQNO = #FRS_MDM_SQNO# --> AND T1.ACP_DT BETWEEN #REF_YR# || '0101' AND #REF_YR# || '1231' AND T1.SRNO = #REF_SRNO# AND T1.ORG_CD = #REF_ORG_CD# ORDER BY T1.ORG_CD ASC , T1.SRNO DESC