SELECT T1.LINK_YR /* 연계년도 */,
    T1.SRECS_DEPT_CD /* 심사재심의부서코드 */,
    F_DPT_INFO(T1.SRECS_DEPT_CD, '1') AS CNSTN_MBCMT_DEPT_NM /* 자문위원부서명 */,
    T1.FRS_MDM_SQNO /* 포렌식매체순번 */,
    (SELECT TO_CHAR(TO_DATE(T3.TRF_DT),'YYYY-MM-DD') || '(' || F_DPT_INFO(T3.TRF_BEF_CHR_DPT_CD, '1') || '->' || F_DPT_INFO(T1.SRECS_DEPT_CD, '1') || ')' AS SRECS_DEPT_CD /* 심사재심의부서코드 */
FROM TBBADHAB002H T3 WHERE T3.CVLCPT_TASK_SECD = '11' AND T3.REF_YR = T1.LINK_YR AND T3.REF_SRNO = T1.FRS_MDM_SQNO AND T3.CMPTN_YN = 'Y' ) AS TRF_BEF_AF_DPT , (SELECT DECODE(MAX(REL_NO),'','N','Y')
FROM TBBADHAB006M T2 WHERE T2.LINK_YR = T1.LINK_YR AND T2.SRECS_DEPT_CD = T1.SRECS_DEPT_CD AND T2.REL_NO = T1.FRS_MDM_SQNO AND T2.REL_TSK_CD = '11') AS REL_NO_YN , T1.NP_NM /*신문명*/ , T1.NEWS_DT /*보도일*/ , T1.SRECS_REL_INST_CD /*관계기관코드*/ , F_ORG_INFO(T1.SRECS_REL_INST_CD, '1') AS REL_INST_NM , T1.RSCH_ASMT_TTL /*제목명*/ , T1.NEWS_DVSN_NM /*보도구분명*/ , T1.PRCS_PIC_ENO /*처리자전산번호*/ , F_USR_INFO(T1.PRCS_PIC_ENO, '2') AS HNDL_NM , T1.PRCS_STTS_CN /*처리상황내용*/ , T1.HNDL_STT_FDT /*처리상황기일*/ , T1.HNDL_STT_DT /*처리상황일*/ , T1.PRCS_YMD /*처리일*/ , T1.APV_DT /*결재일*/ , T1.SRECS_CMT_LDRV_MBCMT_NM /*위원회주심위원명*/ , T1.CMT_SBCN_DT /*위원회부의일*/ , T1.CMT_VT_KND_NM /*위원회의결종류명*/ , T1.ENFC_YMD /*시행일*/ , T1.RMRK_CN /*비고*/ , T1.ADT_DATA_YR /*감사자료년도*/ , T1.ADT_DATA_DEPT_CD /*감사자료부서코드*/ , T1.ADT_DATA_SQNO /*감사자료일련번호*/ , T1.FRST_USER_ID /*최초사용자ID*/ , T1.LAST_USER_ID /*최종사용자ID*/ , T1.LAST_PRCS_DEPT_CD /*최종거래부서코드*/ , T1.LAST_MENU_ID /*최종메뉴ID*/ , T1.FRST_REG_DT /*최초등록일시*/ , T1.LAST_MDFCN_DT /*최종수정일시*/ FROM TBBADHAB004M T1 WHERE 1=1 AND SRECS_DEPT_CD IN (SELECT SRECS_DEPT_CD /* 심사재심의부서코드 */
FROM TABLE(F_AUTH_DEPT(#strCnb#, #strDptAuth#, '', #strDptCd#))) AND LINK_YR = #LINK_YR# AND SRECS_DEPT_CD = #SRECS_DEPT_CD# AND FRS_MDM_SQNO = #FRS_MDM_SQNO# AND T1.LINK_YR = #LINK_YR# AND T1.FRS_MDM_SQNO = #FRS_MDM_SQNO# AND T1.SRECS_DEPT_CD = #SRECS_DEPT_CD# ORDER BY T1.NEWS_DT DESC, LINK_YR, SRECS_DEPT_CD, FRS_MDM_SQNO DESC