SELECT T1.LINK_YR /* 연계년도 */,
    T1.SRECS_DEPT_CD /* 심사재심의부서코드 */,
    F_DPT_INFO(T1.SRECS_DEPT_CD, '1') AS CNSTN_MBCMT_DEPT_NM /* 자문위원부서명 */,
    T1.FRS_MDM_SQNO /* 포렌식매체순번 */,
    (SELECT TO_CHAR(TO_DATE(T3.TRF_DT),'YYYY-MM-DD') || '(' || F_DPT_INFO(T3.TRF_BEF_CHR_DPT_CD, '1') || '->' || F_DPT_INFO(T1.SRECS_DEPT_CD, '1') || ')' AS SRECS_DEPT_CD /* 심사재심의부서코드 */
FROM TBBADHAB002H T3 WHERE T3.CVLCPT_TASK_SECD = '08' AND T3.REF_YR = T1.LINK_YR AND T3.REF_SRNO = T1.FRS_MDM_SQNO AND T3.CMPTN_YN = 'Y' ) AS TRF_BEF_AF_DPT , (SELECT DECODE(MAX(REL_NO),'','N','Y')
FROM TBBADHAB006M T2 WHERE T2.LINK_YR = T1.LINK_YR AND T2.SRECS_DEPT_CD = T1.SRECS_DEPT_CD AND T2.REL_NO = T1.FRS_MDM_SQNO AND T2.REL_TSK_CD = '08') AS REL_NO_YN , T1.OPIN_DVSN_NM /*의견구분명*/ , T1.NTFCTN_RCPT_YMD /*접수일*/ , T1.SRECS_REL_INST_CD /*관계기관코드*/ , F_ORG_INFO(T1.SRECS_REL_INST_CD, '1') AS PROF_RBPRSN_INST_NM /*관계기관명*/ , T1.RSCH_ASMT_TTL /*제목*/ , T1.PRCS_PIC_ENO /*처리자전산번호*/ , F_USR_INFO(T1.PRCS_PIC_ENO, '2') AS USER_NM /*처리담당자명*/ , T1.PRCS_STTS_CN /*처리상황내용*/ , T1.HNDL_STT_FDT /*처리상황기일*/ , T1.HNDL_STT_DT /*처리상황일*/ , T1.PRCS_YMD /*처리일*/ , T1.APV_DT /*결재일*/ , T1.SRECS_CMT_LDRV_MBCMT_NM /*위원회주심위원명*/ , T1.CMT_SBCN_DT /*위원회부의일*/ , T1.CMT_VT_KND_NM /*위원회의결종류명*/ , T1.ENFC_YMD /*시행일*/ , T1.RMRK_CN /*비고*/ FROM TBBADHAB005M T1 /*의견표시사항*/ WHERE 1=1 AND SRECS_DEPT_CD IN (SELECT SRECS_DEPT_CD /* 심사재심의부서코드 */
FROM TABLE(F_AUTH_DEPT(#strCnb#, #strDptAuth#, '', #strDptCd#))) AND LINK_YR = #LINK_YR# AND SRECS_DEPT_CD = #SRECS_DEPT_CD# AND FRS_MDM_SQNO = #FRS_MDM_SQNO# AND T1.LINK_YR = #LINK_YR# AND T1.SRECS_DEPT_CD = #SRECS_DEPT_CD# AND T1.FRS_MDM_SQNO = #FRS_MDM_SQNO# ORDER BY T1.NTFCTN_RCPT_YMD DESC, LINK_YR DESC, SRECS_DEPT_CD, FRS_MDM_SQNO DESC