UPDATE TWFAACAV019M SET RB_CD = SUBSTR(#TRF_AF_CHR_DPT_CD#,0,3) /* 소관국코드 */,  PROF_RBPRSN_CPD_SECD = SUBSTR(#TRF_AF_CHR_DPT_CD#,4,3) /* 증명책임자소관과구분코드 */,  LAST_USER_ID = #LAST_USER_ID# /* 최종사용자아이디 */,  LAST_PRCS_DEPT_CD = #LAST_PRCS_DEPT_CD# /* 최종처리부서코드 */,  LAST_MENU_ID = #LAST_MENU_ID# /* 최종메뉴아이디 */,  LAST_MDFCN_DT = SYSDATE /* 최종수정일시 */WHERE 1=1 AND PROF_YM = #PROF_YM# /*증명년월*/ AND JRSD_CD = #JRSD_CD# /*소관코드*/ AND ACNTG_CD = #ACNTG_CD# /*회계코드*/ AND BLL_KDCD = #BLL_KDCD# /*계산서코드*/ AND PROF_RBPRSN_SQNO = #PROF_RBPRSN_SQNO# /*증명책임자일련번호*/ AND GVMNFC_OPER_EPS_RCDB_PBOFC_SQNO = #GVMNFC_OPER_EPS_RCDB_PBOFC_SQNO# /*일상경비출납공무원일련번호*/