UPDATE TB_BADDED003M /* 국과별감사처리상황기본 */ SET ADT_OW_PRCS_DEPT_SQNO = ( SELECT NVL(MAX(T2.ADT_OW_PRCS_DEPT_SQNO),0) + 1 AS ADT_OW_PRCS_DEPT_SQNO /* 감사사무처리부서순번 */
FROM TB_BADDED001M T1 ,TB_BADDED003M T2 WHERE T1.CVLCPT_ADT_YR = T2.ADT_YR AND T1.ADT_NO = T2.ADT_NO AND T1.CVLCPT_ADT_YR = #CVLCPT_ADT_YR# AND T1.MNG_DEPT_CD = #MNG_DEPT_CD#) WHERE CVLCPT_ADT_YR = #CVLCPT_ADT_YR# /* 감사년도 */ AND ADT_NO = #ADT_NO# /* 감사번호 */