/* csp/acp/002/CSPACP002EDetailSelect */ SELECT A.GDS_RQS_SNO,
    /*물품요청순번*/ A.GDS_DVSN_CD,
    /*물품구분코드*/ A.DMND_YMD AS DMND_YMD /* 요청일자 */,
    /*요청일*/ A.RQSTR_ENO AS RQSTR_ENO /* 요청자직원전산번호 */,
    /*요청자전산번호*/ (SELECT AS USER_NM /* 사용자명 */
FROM TBDCMACM001M Z WHERE A.RQSTR_ENO = Z.TRGT_NOP_ENO ) AS RQR_NAM, /*요청자명*/ A.RQS_TIT_NM, /*요청제목명*/ A.DMND_CN, /*요청내용*/ A.PRCS_YMD, /*처리일*/ A.PRCS_PIC_ENO, /*처리자전산번호*/ (SELECT USER_NM /* 사용자명 */
FROM TBDCMACM001M Z WHERE A.PRCS_PIC_ENO = Z.TRGT_NOP_ENO ) AS HNDL_PEN_NAM, /*처리자명*/ A.PRCS_CN, /*처리내용*/ (SELECT CVLCPT_DOC_ID /* 민원문서아이디 */
FROM TBCSPACP002M Z WHERE Z.GDS_RQS_SNO = A.GDS_RQS_SNO AND Z.GDS_RQS_ATT_FL_DVSN_CD = '1') AS DMND_DOC_ID, /*요청문서ID*/ (SELECT CVLCPT_DOC_ID /* 민원문서아이디 */
FROM TBCSPACP002M Z WHERE Z.GDS_RQS_SNO = A.GDS_RQS_SNO AND Z.GDS_RQS_ATT_FL_DVSN_CD = '2') AS CRU_PRCS_DOC_ID, /*처리문서ID*/ F_DPT_INFO(A.DMND_DEPT_CD,'1') AS RQS_DPT_NM, (SELECT CRU_TELNO /* 부패전화번호 */
FROM TBDCMACM001M WHERE TRGT_NOP_ENO = A.RQSTR_ENO ) AS CRU_TELNO, A.PRCS_PIC_ENO AS OLD_HNDL_PEN_CNB FROM TBCSPACP001M A WHERE A.GDS_RQS_SNO = #strGDS_RQS_SNO# AND A.GDS_KBN='1'