/* csp/acp/004/CSPACP004MainPSelect */ SELECT TO_CHAR(TO_DATE(A.DMND_YMD),'YYYY-MM-DD') AS DMND_YMD /* 요청일자 */,
    /*요청일*/ A.RQSTR_ENO AS RQSTR_ENO /* 요청자직원전산번호 */,
    /*요청자전산번호*/ ( SELECT S1.ABR_DPT_NM_1
FROM TBDCMACM002M S1 WHERE S1.SRECS_DEPT_CD = A.DMND_DEPT_CD ) ||' '|| ( SELECT Z.USER_NM /* 사용자명 */
FROM TBDCMACM001M Z WHERE A.RQSTR_ENO = Z.TRGT_NOP_ENO ) AS RQR_NAM, /*요청자명*/ A.RQS_TIT_NM, /*요청제목명*/ A.DMND_CN, /*요청내용*/ TO_CHAR(TO_DATE(A.PRCS_YMD),'YYYY-MM-DD') AS PRCS_YMD, /*처리일*/ A.PRCS_PIC_ENO, /*처리자전산번호*/ ( SELECT F_DPT_INFO(Z.SRECS_DEPT_CD,'4')||' '||USER_NM AS SRECS_DEPT_CD /* 심사재심의부서코드 */
FROM TBDCMACM001M Z WHERE A.PRCS_PIC_ENO = Z.TRGT_NOP_ENO ) AS HNDL_PEN_NAM, /*처리자명*/ A.PRCS_CN /*처리내용*/ FROM TBCSPACP001M A WHERE A.GDS_RQS_SNO = #strGDS_RQS_SNO# AND A.GDS_KBN = '2' /*소모품*/