INSERT INTO TBCSPAOE017D (EVL_YR, HLYR_DVSN_CD, CVLCPT_TASK_SECD, AUD_TSK_KND_CD, CHGR_DVSN_CD, PIC_ENO, EVL_CMTR_NO, BLT_DPT_CD, JBGD_SECD, FRST_USER_ID, LAST_USER_ID, LAST_PRCS_DEPT_CD, LAST_MENU_ID, FRST_REG_DT, LAST_MDFCN_DT) SELECT #strEvlYr#,
    #strHlyrDvsnCd#,
    #CVLCPT_TASK_SECD#,
    #strAudTskKndCd#,
    #strChgrDvsnCd#,
    D1.TRGT_NOP_ENO /* 대상인원직원전산번호 */,
    ( ROWNUM - 1 ) + (SELECT NVL(MAX(EVL_CMTR_NO), 0) + 1
FROM TBCSPAOE017D WHERE EVL_YR = #strEvlYr# AND HLYR_DVSN_CD = #strHlyrDvsnCd# AND CVLCPT_TASK_SECD = #CVLCPT_TASK_SECD# AND AUD_TSK_KND_CD = #strAudTskKndCd# AND CHGR_DVSN_CD = #strChgrDvsnCd# ) AS EVL_CMTR_NO , D1.SRECS_DEPT_CD , D1.JBGD_SECD , #FRT_USR_ID# , #FNL_USR_ID# , #FNL_DEAL_DPT_CD# , #FNL_MNU_ID# , SYSDATE , SYSDATE FROM TBDCMACM001M D1 , TBDCMACM002M D2 WHERE D1.SRECS_DEPT_CD = D2.SRECS_DEPT_CD AND D2.USE_YN = 'Y' /*사용여부*/ AND ( ( D2.DEPT_TPCD = 'A' AND D1.JBPS_CD IN ('0015800', '0013600', '0035100')) /*국장, 과장, 단장*/ OR ( D2.DEPT_TPCD = 'S' AND D1.JBPS_CD IN ('0013600', '0096800', '0035400','0035100', '0067400', '0058100', '0015800', '0013900', '0020600', '0037500')) /*과장,팀장,담당관, 단장, 원장, 실장, 관리관, 국장, 기획조정실장(실장)(기획실장), 대변인(국장)*/ ) AND D1.SRECS_DEPT_CD NOT IN ('770770', '500000','300210', '300220', '300230', '100100' ) /*감사원, 감사원장, 연구1,2,3팀, 감찰담당관 평가대상제외 */ AND D1.HDOF_SECD = 'AAA' /*재직*/