SELECT CASE WHEN CTB_RTE_CFM_DVCHF_CNB IS NOT NULL THEN 'Y' ELSE 'N' END AS CTB_RTE_CFM_DVCHF_CNB /* 기여율 과장 확인 여부 */,
    CASE WHEN CTB_RTE_CFM_BRUCHF_CNB IS NOT NULL THEN 'Y' ELSE 'N' END AS CTB_RTE_CFM_BRUCHF_CNB /* 기여율 국장 확인 여부 */,
    NVL((SELECT COUNT(ADT_STP_SECD) AS ADT_STP_SECD /* 감사단계구분코드 */
FROM TBCSPAOE023M WHERE CVLCPT_ADT_YR = #CVLCPT_ADT_YR# AND TSK_NO = #strTskNo# AND ADT_STP_SECD = #ADT_STP_SECD# GROUP BY CVLCPT_ADT_YR, TSK_NO, ADT_STP_SECD),0) AS PTCPT_CNB_CNT /*참여 인원 수*/ FROM TBCSPAOE022M WHERE 1=1 AND CVLCPT_ADT_YR = #CVLCPT_ADT_YR# AND TSK_NO = #strTskNo# AND ADT_STP_SECD = (SELECT MAX(ADT_STP_SECD) AS ADT_STP_SECD /* 감사단계구분코드 */
FROM TBCSPAOE022M WHERE 1=1 AND CVLCPT_ADT_YR = #CVLCPT_ADT_YR# AND TSK_NO = #strTskNo# AND CMPTN_YMD IS NOT NULL)