SELECT D1.CVLCPT_ADT_YR /* 민원감사년도 */,
    D1.TSK_NO /* 감사번호 */,
    D1.ADT_STP_SECD /* 감사단계구분코드 */,
    ( SELECT B.CTB_RTE_CFM_RQS_YN
FROM ( SELECT CVLCPT_ADT_YR /* 민원감사년도 */,
    TSK_NO,
    MAX(ADT_STP_SECD) AS ADT_STP_SECD /* 감사단계구분코드 */
FROM TBCSPAOE022M WHERE CMPTN_YMD IS NOT NULL GROUP BY CVLCPT_ADT_YR, TSK_NO ) A , TBCSPAOE022M B WHERE A.CVLCPT_ADT_YR = B.CVLCPT_ADT_YR AND A.TSK_NO = B.TSK_NO AND A.AUD_STEP_CD_MAX = B.ADT_STP_SECD AND A.CVLCPT_ADT_YR = #CVLCPT_ADT_YR# AND A.TSK_NO = #strTskNo# AND B.CMPTN_YMD IS NOT NULL AND B.CTB_RTE_CFM_RQS_YN IS NOT NULL ) AS CTB_RTE_CFM_RQS_YN /*기여율확인요청여부*/ FROM TBCSPAOE022M D1 , TBCSPAOE021M D2 WHERE 1=1 AND D1.CVLCPT_ADT_YR = D2.CVLCPT_ADT_YR AND D1.TSK_NO = D2.TSK_NO AND D1.CVLCPT_ADT_YR = #CVLCPT_ADT_YR# AND D1.TSK_NO = #strTskNo# /*AND (D1.AUD_STEP_CD NOT IN ('10007') OR D1.AUD_STEP_CD IS NULL ) 신규 추가 2016-05-20 삭제 2019.05.15*/ D2.ADT_MTTR_NM = #ADT_MTTR_NM# AND D1.CMPTN_YMD IS NOT NULL