SELECT A.CD,
    A.CD_NM
FROM TBDCMACM013C A ,TBCSPAOE009M B WHERE 1 = 1 AND A.CD = B.AUD_TSK_KND_CD AND A.CMM_CD_DVSN_CD = '1000272' AND NVL(A.USE_YN, 'N') = 'Y' AND A.CD != '10000' /*공통업무 제외*/ AND A.CD != '000000000' AND B.EVL_YR = #strEvlYr# AND B.HLYR_DVSN_CD = #strHlyrDvsnCd# AND B.CVLCPT_TASK_SECD = #CVLCPT_TASK_SECD# ORDER BY SUBSTR(A.CD_NM, 0, 4), A.OTPT_SEQ, A.CD