select DEPT_CD /* 부서코드 */,
    DEPT_NM /* 부서명 */
from( SELECT SRECS_DEPT_CD AS SRECS_DEPT_CD /* 심사재심의부서코드 */,
    F_DPT_INFO(SRECS_DEPT_CD, '1') AS DEPT_NM /* 부서명 */,
    DPT_SNO
FROM ( SELECT SRECS_DEPT_CD /* 심사재심의부서코드 */,
    DPT_SNO
FROM TBDCMACM002M WHERE DEPT_TPCD = 'S' /*지원부서만 가져온다.*/ AND USE_YN = 'Y' AND SRECS_DEPT_CD NOT IN('141010', '141020', '141030', /* 150522_혁신전략팀,감사혁신,조직혁신팀 */ '120000', '080000', '0D0000', '140000', '100000','300210','300220','300230', '160000', '1A0000', '170000', '190000', '150000','140150','080190','190160', '200000', '300000', '400000', '500000', '510000','100100','080180','080220','200010', '520000', '530000', '540000', '550000', '150170','150160','770770','300010','150150', '121100', '121200', '122100','122200','122300','122400','150180','140130' , '071110', '071120','071130','071140','122500','071000' ,'081000', '081100', '081200', '081300', '081400' ,'071100', '071300' , '180000', '180100', '180110', '180120', '170300', '170320' ) /*평가자평가_지원업무평가관리의 부서명과 동일하게 출력되도록 요청_150911*/ GROUP BY SRECS_DEPT_CD ,DPT_SNO UNION SELECT SRECS_DEPT_CD /* 심사재심의부서코드 */,
    DPT_SNO
FROM TBDCMACM002M WHERE SRECS_DEPT_CD IN ('062500','062600','062700','062800') AND USE_YN = 'Y' ) ORDER BY DPT_SNO )