SELECT D1.RK_CD,
    F_CODE_NM('1000274', D1.RK_CD) AS F_CODE_NM /* 등급코드명 */
FROM ( SELECT RK_CD
FROM TBCSPAOE010D WHERE EVL_YR = #strEvlYr# AND HLYR_DVSN_CD = #strHlyrDvsnCd# AND CVLCPT_TASK_SECD = '10000' AND IDCT_YN = 'Y' GROUP BY EVL_YR, HLYR_DVSN_CD, CVLCPT_TASK_SECD, RK_CD ) D1 , TBDCMACM013C D2 WHERE 1=1 AND D1.RK_CD = D2.CD AND D2.CD <> '000000000' AND D2.CMM_CD_DVSN_CD = '1000274' AND D2.USE_YN = 'Y' ORDER BY D2.OTPT_SEQ, D2.CD