SELECT D1.PTCPT_CNB AS PTCPT_CNB /* 참여자 전산번호 */,
    F_DPT_INFO(#strDeptCd#, '1') AS CNSTN_MBCMT_DEPT_NM /* 자문위원부서명 */,
    F_CODE_NM('1000173', MAX(D1.JBGD_SECD)) AS JBGD_NM /* 직급명 */,
    SUM(D2.WGT_RTE * D1.CTB_RT / 100) AS CTB_RT /* 기여비율 */,
    F_USR_INFO(D1.PTCPT_CNB, 2) AS INFO_RLPR_NM /* 정보관계자명 */
FROM TBCSPAOE026M D2 , TBCSPAOE027M D1 WHERE D2.EVL_YR = D1.EVL_YR AND D2.HLYR_DVSN_CD = D1.HLYR_DVSN_CD AND SUBSTR(D2.CHR_DPT_CD, 0 ,3) = SUBSTR(D1.CHR_DPT_CD, 0 ,3) AND D2.CHR_DPT_CD = #strDeptCd# AND SUBSTR(D2.CHR_DPT_CD, 0 ,3) = SUBSTR(D1.CHR_DPT_CD, 0 ,3) AND D2.CHR_DPT_CD = #strDeptCd# AND D2.CHR_DPT_CD = D1.CHR_DPT_CD AND D2.CHR_DPT_CD = #strDeptCd# AND SUBSTR(D2.CHR_DPT_CD, 0 ,3) = SUBSTR(D1.CHR_DPT_CD, 0 ,3) AND D2.CHR_DPT_CD = #strDeptCd# AND D2.TSK_NO = D1.TSK_NO AND D1.EVL_YR = #strEvlYr# AND D1.HLYR_DVSN_CD = #strHlyrDvsnCd# GROUP BY D1.PTCPT_CNB