SELECT D1.RK_CD /* 등급코드 */,
    D2.CD_NM AS CD_NM
FROM TBCSPAOE010D D1 , TBDCMACM013C D2 WHERE 1=1 AND D1.RK_CD = D2.CD AND D2.CD <> '000000000' AND D2.CMM_CD_DVSN_CD = '1000274' AND D2.USE_YN = 'Y' AND D1.EVL_YR = #strEvlYr# AND D1.HLYR_DVSN_CD = #strHlyrDvsnCd# AND D1.CVLCPT_TASK_SECD = '10003' /*운영직업무*/ AND D1.AUD_TSK_KND_CD = '10000' /*공통*/ AND D1.IDCT_YN = 'Y' ORDER BY D2.OTPT_SEQ, D2.CD