SELECT NVL(A.ST_DRIVE_RTE, 0) AS ST_DRIVE_RTE /* 국단장 감사단계 이전 추진율 */,
    NVL(B.END_DRIVE_RTE, 0) AS END_DRIVE_RTE /* 심의실 감사단계 이후 추진율 2015.03.16 NVL처리 안해줘서 오류있었음. */
FROM ( SELECT SUM(DRIVE_RTE) AS DRIVE_RTE
FROM TBCSPAOE008D WHERE EVL_YR = #strEvlYr# AND HLYR_DVSN_CD = #strHlyrDvsnCd# AND CVLCPT_TASK_SECD = #CVLCPT_TASK_SECD# AND ADT_KDCD = #ADT_KDCD# AND ADT_STP_SECD BETWEEN #strAudStStepCd# AND CASE WHEN TO_NUMBER(#strAudEndStepCd#) = 10006 THEN #strAudStStepCd# ELSE '10006' END AND #strAudEndStepCd# ) B