SELECT D2.TSK_NM,
    D2.WGT_RTE,
    D3.CTB_RT /* 기여비율 */
FROM TBCSPAOE026M D2 , TBCSPAOE027M D3 WHERE 1=1 AND D2.EVL_YR = D3.EVL_YR AND D2.HLYR_DVSN_CD = D3.HLYR_DVSN_CD AND D2.CHR_DPT_CD = CASE WHEN SUBSTR(D3.CHR_DPT_CD,1,3) = '141' THEN '141000' WHEN SUBSTR(D3.CHR_DPT_CD,1,3) = '121' THEN '121000' WHEN SUBSTR(D3.CHR_DPT_CD,1,3) = '122' AND D3.CHR_DPT_CD = '122500' THEN D3.CHR_DPT_CD WHEN SUBSTR(D3.CHR_DPT_CD,1,3) = '122' AND D3.CHR_DPT_CD <> '122500' THEN '122000' ELSE D3.CHR_DPT_CD END AND D2.TSK_NO = D3.TSK_NO AND D2.EVL_YR = #strEvlYr# AND D2.HLYR_DVSN_CD = #strHlyrDvsnCd# AND D3.PTCPT_CNB = #strEvlCnb# ORDER BY D2.TSK_NO