UPDATE TBCSPAOE041M SET LAST_MDFCN_DT = SYSDATE /* 최종수정일시 */,  DPT_DVSN_CD = #DPT_DVSN_CD# ,  EVL_DPT_CD = #EVL_DPT_CD# ,  EVL_DPT_NM = #EVL_DPT_NM# ,  JBPS_CD = #JBPS_CD# /* 직위코드 */,  INFO_RLPR_NM = #INFO_RLPR_NM# /* 정보관계자명 */,  JBGD_SECD = #JBGD_SECD# /* 직급구분코드 */,  HDOF_SECD = #HDOF_SECD# /* 재직구분코드 */,  WRK_STR_DT = #WRK_STR_DT# ,  WRK_END_DT = #WRK_END_DT# ,  WRK_DCN = GREATEST((TO_DATE(#WRK_END_DT#, 'YYYYMMDD') - TO_NUMBER(#WRK_EXMP_DCN#)) - TO_DATE(#WRK_STR_DT#, 'YYYYMMDD')) + 1 /*근무제외일수를 뺀 근무일수를 계산한다*/ ,  RMRK_CN = #RMRK_CN# /* 비고내용 */,  EVL_TG_YN = DECODE(NVL(#EVL_TG_YN#, 'Y'), '', 'N', #EVL_TG_YN#) ,  WRK_EXMP_DCN = NVL(#WRK_EXMP_DCN#, 0) ,  WRK_DCN_TXT = #WRK_DCN_TXT# LAST_USER_ID = #LAST_USER_ID# LAST_PRCS_DEPT_CD = #LAST_PRCS_DEPT_CD# LAST_MENU_ID = #LAST_MENU_ID# WHERE 1 = 1 AND EVL_YR = #EVL_YR# AND HLYR_DVSN_CD = #HLYR_DVSN_CD# AND TRGT_NOP_ENO = #TRGT_NOP_ENO#