SELECT F_DPT_INFO(D2.SRECS_DEPT_CD, '1') AS CNSTN_MBCMT_DEPT_NM /* 자문위원부서명 */,
    NVL2(D1.CHR_DPT_CD, '등록', '미등록') AS NVL2
FROM ( SELECT CHR_DPT_CD
FROM TBCSPAOE026M WHERE EVL_YR = #strEvlYr# AND HLYR_DVSN_CD = #strHlyrDvsnCd# GROUP BY CHR_DPT_CD ) D1 , ( SELECT SRECS_DEPT_CD /* 심사재심의부서코드 */
FROM TBDCMACM002M WHERE DEPT_TPCD = 'S' /*지원부서만*/ AND USE_YN = 'Y' AND SRECS_DEPT_CD NOT IN ('141010', '141020', '141030', '120000', '080000', '0D0000', '140000', '100000','300210','300220','300230', '160000', '1A0000', '170000', '190000', '150000','140150','080190','190160', '200000', '300000', '400000', '500000', '510000','100100','080180','080220','200010', '520000', '530000', '540000', '550000', '150170','150160','770770','300010','150150', '121100', '121200', '122100','122200','122300','122400','150180','140130' , '071110', '071120','071130','071140','122500','071000' ,'081000', '081100', '081200', '081300', '081400' ,'071100', '071300' , '180000', '180100', '180110', '180120', '170300', '170320') UNION SELECT '121000' AS SRECS_DEPT_CD /* 심사재심의부서코드 */
FROM DUAL UNION SELECT '122000' AS SRECS_DEPT_CD /* 심사재심의부서코드 */
FROM DUAL /* 2015.05.20 혁신전략팀, 감사혁신팀, 조직혁신팀 제외하고 감사혁신추진단으로 표시 시작 */ /*2015.05.25 인사혁신과, 운영지원과*/ ) D2 WHERE D1.CHR_DPT_CD (+) = D2.SRECS_DEPT_CD AND NVL2(D1.CHR_DPT_CD, 'Y', 'N') = #strYn# ORDER BY NVL2(D1.CHR_DPT_CD, 2, 1), D2.SRECS_DEPT_CD