    DECODE(D.TPOF_ATRZ_SECD, 'AudInfoSend', E.USER_NM, NULL) AS TPOF_ATRZ_SECD /* 제보결재구분코드 */,
    DECODE(D.TPOF_ATRZ_SECD, 'AudInfoTrnf', F_CODE_NM('1000176', E.JBPS_CD), NULL) AS TPOF_ATRZ_SECD /* 제보결재구분코드 */,
    DECODE(D.TPOF_ATRZ_SECD, 'AudInfoTrnf', F_CODE_NM('1000173', E.JBGD_SECD), NULL) AS TPOF_ATRZ_SECD /* 제보결재구분코드 */,
    DECODE(D.TPOF_ATRZ_SECD, 'AudInfoTrnf', E.USER_NM, NULL) AS TPOF_ATRZ_SECD /* 제보결재구분코드 */,
    DECODE(D.TPOF_ATRZ_SECD, 'AudInfoHndl', F_CODE_NM('1000176', E.JBPS_CD), NULL) AS TPOF_ATRZ_SECD /* 제보결재구분코드 */,
    DECODE(D.TPOF_ATRZ_SECD, 'AudInfoHndl', F_CODE_NM('1000173', E.JBGD_SECD), NULL) AS TPOF_ATRZ_SECD /* 제보결재구분코드 */,
    DECODE(D.TPOF_ATRZ_SECD, 'AudInfoHndl', E.USER_NM, NULL) AS TPOF_ATRZ_SECD /* 제보결재구분코드 */,
