FROM TBDCMACM022L D /* 결재문서내역 */ , TBDCMACM001M E /* 사용자정보기본 */ WHERE D.FNL_APV_CNB = E.TRGT_NOP_ENO AND D.ATRZ_STTS_SECD = '3' /* [3:결재승인]*/ AND D.TPOF_ATRZ_SECD IN ('AudInfoSend', 'AudInfoHndl', 'AudInfoTrnf') /* [AudInfoSend:감사정보제출, AudInfoHndl:감사정보처리] */ ) D WHERE A.INFO_YR = B.INFO_YR AND A.FRS_MDM_SQNO = B.FRS_MDM_SQNO AND (CASE WHEN A.ADT_INFO_PRGRS_STTS_SECD > '14' AND A.ADT_INFO_PRGRS_STTS_SECD = '22' THEN A.ATRZ_DOC_NO ELSE A.ATRZ_DOC_NO END) = D.ATRZ_DOC_NO(+) AND A.INFO_YR = #INFO_YR# AND A.FRS_MDM_SQNO = #FRS_MDM_SQNO# AND (A.INFO_YR, A.FRS_MDM_SQNO) IN (WITH VW AS (SELECT REGEXP_SUBSTR(#strYeList#, '[^||]+', 1, LEVEL) AS INFO_YR /* 정보년도 */,
