SELECT /* csp.bii.service.impl.CSPBII165QServiceImpl.CSPBII165QMainSelect */ A.INFO_YR || '-' || A.INFO_SQNO AS INFO_YR /* 정보년도 */,
    A.SBMSN_YMD /* 제출일자 */,
    CASE WHEN SUBSTR(F_CODE_NM('1000097', A.INFO_SECD), 1, 2) is not null THEN '(' || SUBSTR(F_CODE_NM('1000097', A.INFO_SECD), 1, 2) || ')' || A.INFO_NM ELSE A.INFO_NM END AS DSPS_RQT_NM /* 처분요구명 */,
    (SELECT B.USER_NM AS USER_NM /* 사용자명 */
FROM TBDCMACM001M B WHERE B.TRGT_NOP_ENO = A.PRSNR_ENO) AS PRSNR_NM /* 제출자성명 */ , F_CODE_NM('1000097', A.INFO_SECD) AS INF_DVSN_NM /* 정보구분명 */ , F_CODE_NM('1000040', A.SRC_SECD) AS SRC_DVSN_NM /* 출처구분명 */ , A.ADT_INFO_PRGRS_STTS_SECD /* 감사정보진행상태코드 */ , A.INFO_YR , A.INFO_SQNO , A.RTSO_YMD , A.OVRL_PIC_ENO , F_USR_INFO(A.OVRL_PIC_ENO,'2') AS BLE_CHGR_NAM , (SELECT F_CODE_NM('1000852',GEN_ADMN_DVSN_CD)
FROM TBDCMACM001M S1 WHERE A.PRSNR_ENO = S1.TRGT_NOP_ENO ) AS GEN_ADMN_DVSN_NM ,TO_CHAR(TO_DATE(NTFCTN_RCPT_YMD),'YYYY-MM-DD') AS NTFCTN_RCPT_YMD ,TO_CHAR(TO_DATE(HNDT_YMD),'YYYY-MM-DD') AS HNDT_YMD FROM TB_CSPBII170M A WHERE 1=1 AND A.ADT_INFO_PRGRS_STTS_SECD = '05' /* 감사정보진행상태코드[05:제출완료] */ AND A.ADT_INFO_PRGRS_STTS_SECD = '10' /* 감사정보진행상태코드[05:제출완료] */ AND A.PRSNR_ENO IN (SELECT S1.TRGT_NOP_ENO /* 대상인원직원전산번호 */
FROM TBDCMACM001M S1 WHERE S1.GEN_ADMN_DVSN_CD = #strGEN_ADMN_DVSN_CD#) ORDER BY A.INFO_YR , A.INFO_SQNO