SELECT A.INFO_YR /* 정보년도 */,
    A.FRS_MDM_SQNO /* 포렌식매체순번 */,
    A.INFO_SECD /* 정보구분코드 */,
    '3' AS PRCS_DRCTN_SECD /* 처리지시구분코드 */,
    A.ETC_LBSVC_MNG_DEPT_CD /* 기타용역관리부서코드 */,
    nvl(A.CSIT_SCR,0) AS CSIT_SCR /* 충실성점수 */,
    nvl(A.RLBT_SCR,0) AS RLBT_SCR /* 신뢰성점수 */,
    nvl(A.UTLZ_SCR,0) AS UTLZ_SCR /* 활용점수 */,
    TO_CHAR(TO_DATE(SBMSN_YMD),'YYYY.MM.DD') AS SBMSN_YMD /* 제출일자 */,
    (SELECT B.CNSTN_MBCMT_HOME_TELNO AS CNSTN_MBCMT_HOME_TELNO /* 자문위원자택전화번호 */
FROM TBDCMACM001M B WHERE A.PRSNR_ENO = B.TRGT_NOP_ENO ) AS CNSTN_MBCMT_HOME_TELNO ,A.DSPS_RQT_NM ,(SELECT B.JBGD_SECD /* 직급구분코드 */
FROM TBDCMACM001M B WHERE A.PRSNR_ENO = B.TRGT_NOP_ENO ) AS JBGD_SECD ,(SELECT B.USER_NM /* 사용자명 */
FROM TBDCMACM001M B WHERE A.PRSNR_ENO = B.TRGT_NOP_ENO ) AS USER_NM ,F_USR_INFO(OVRL_PIC_ENO,'2') AS BLE_CHGR_NAM , nvl(A.CSIT_ACT_MNBD_SCR,0) AS CSIT_ACT_MNBD_SCR /* 충실성행위주체점수 */ , nvl(A.CSIT_ACT_HR_SCR,0) AS CSIT_ACT_HR_SCR /* 충실성행위시기점수 */ , nvl(A.CSIT_ACT_TRGT_SCR,0) AS CSIT_ACT_TRGT_SCR /* 충실성행위대상점수 */ , nvl(A.CSIT_ACT_CN_SCR,0) AS CSIT_ACT_CN_SCR /* 충실성행위대상점수 */ , nvl(A.CSIT_LGL_BSS_SCR,0) AS CSIT_LGL_BSS_SCR /* 충실성법적근거점수 */ , nvl(A.RLBT_EVI_SCR,0) AS RLBT_EVI_SCR /* 신뢰성증거자료점수 */ , nvl(A.RLBT_IBQ_SCR,0) AS RLBT_IBQ_SCR /* 신뢰성탐문정보점수 */ , nvl(A.UTLZ_INS_SCR,0) AS UTLZ_INS_SCR /* 활용가능성감찰정보점수 */ , nvl(A.UTLZ_SPCF_SCR,0) AS UTLZ_SPCF_SCR /* 활용가능성특정정보점수 */ , nvl(A.CSIT_ACT_MNBD_SCR,0) + nvl(A.CSIT_ACT_HR_SCR,0) + nvl(A.CSIT_ACT_TRGT_SCR,0) + nvl(A.CSIT_ACT_CN_SCR,0) + nvl(A.CSIT_LGL_BSS_SCR,0) AS CSIT_TSC , nvl(A.RLBT_EVI_SCR,0) + nvl(A.RLBT_IBQ_SCR,0) AS RLBT_WHOL_SCR , nvl(A.UTLZ_INS_SCR,0) + nvl(A.UTLZ_SPCF_SCR,0) AS UTLZ_WHOL_SCR , A.RLS_YN , (SELECT B.CNSTN_MBCMT_DEPT_NM /* 자문위원부서명 */
FROM TBDCMACM002M B WHERE B.SRECS_DEPT_CD = A.ETC_LBSVC_MNG_DEPT_CD) AS PRCS_DEPT_NM /* 처리(소관)부서명 */ , A.TRNF_ATRZ_DOC_NO , F_CODE_NM('1000085', A.ADT_INFO_PRGRS_STTS_SECD) AS TRNF_APV_STA_NM /* 감사정보진행상태명 */ FROM TB_CSPBII100M A WHERE A.NTFCTN_RCPT_YMD BETWEEN #strStrAcpDt# AND #strEndAcpDt# AND A.OVRL_PIC_ENO = #OVRL_PIC_ENO# AND A.ADT_INFO_PRGRS_STTS_SECD IN ('10','13') AND A.ADT_INFO_PRGRS_STTS_SECD = '16' AND A.ADT_INFO_PRGRS_STTS_SECD = '18' ORDER BY CASE WHEN CSIT_TSC >0 THEN 1 WHEN RLBT_WHOL_SCR >0 THEN 1 WHEN UTLZ_WHOL_SCR >0 THEN 1 END ,A.INFO_YR ,A.FRS_MDM_SQNO