SELECT ROWNUM,
    T1.*
FROM( SELECT A.INFO_YR /* 정보년도 */,
    A.INFO_SQNO /* 정보순번 */,
    A.INFO_YR || '-' || A.INFO_SQNO AS INFO_YR /* 정보년도 */,
    A.INFO_NM /* 정보명 */,
    F_CODE_NM('1000173',(SELECT AS JBGD_SECD /* 직급구분코드 */
FROM TBDCMACM001M WHERE 1=1 AND TRGT_NOP_ENO =A.PRSNR_ENO)) AS JBGD_NM, ( SELECT USER_NM /* 사용자명 */
FROM TBDCMACM001M WHERE 1=1 AND TRGT_NOP_ENO =A.PRSNR_ENO ) AS PRSNR_NM, F_USR_INFO(OVRL_PIC_ENO, '2') AS BLE_CHGR_NM, F_CODE_NM('1000097',A.INFO_SECD) AS INF_DVSN_NM, F_CODE_NM('1000039',A.PRCS_DRCTN_SECD) AS HNDL_ORD_NM/* 처리지시코드[3:감사대상] */, ( SELECT CNSTN_MBCMT_DEPT_NM /* 자문위원부서명 */
FROM TBDCMACM002M S WHERE 1=1 AND S.SRECS_DEPT_CD = A.ETC_LBSVC_MNG_DEPT_CD /* 처리부서코드 */ ) AS PRCS_DEPT_NM, nvl(A.CSIT_ACT_MNBD_SCR, 0) + nvl(A.CSIT_ACT_HR_SCR, 0) + nvl(A.CSIT_ACT_TRGT_SCR, 0) + nvl(A.CSIT_ACT_CN_SCR, 0) + nvl(A.CSIT_LGL_BSS_SCR, 0) /*충실성소계*/ +nvl(A.RLBT_EVI_SCR, 0) + nvl(A.RLBT_IBQ_SCR, 0) /*신뢰성소계*/ +nvl(A.UTLZ_INS_SCR, 0) + nvl(A.UTLZ_SPCF_SCR, 0) /*활용가능성소계*/ AS TOT_SC, nvl(A.CSIT_ACT_MNBD_SCR, 0) + nvl(A.CSIT_ACT_HR_SCR, 0) + nvl(A.CSIT_ACT_TRGT_SCR, 0) + nvl(A.CSIT_ACT_CN_SCR, 0) + nvl(A.CSIT_LGL_BSS_SCR, 0) AS CSIT_TSC, nvl(A.CSIT_ACT_MNBD_SCR, 0) AS CSIT_ACT_MNBD_SCR /* 충실성행위주체점수 */, nvl(A.CSIT_ACT_HR_SCR, 0) AS CSIT_ACT_HR_SCR /* 충실성행위시기점수 */, nvl(A.CSIT_ACT_TRGT_SCR, 0) AS CSIT_ACT_TRGT_SCR /* 충실성행위대상점수 */, nvl(A.CSIT_ACT_CN_SCR, 0) AS CSIT_ACT_CN_SCR /* 충실성행위대상점수 */, nvl(A.CSIT_LGL_BSS_SCR, 0) AS CSIT_LGL_BSS_SCR /* 충실성법적근거점수 */, nvl(A.RLBT_EVI_SCR, 0) + nvl(A.RLBT_IBQ_SCR, 0) AS RLBT_WHOL_SCR , nvl(A.RLBT_EVI_SCR, 0) AS RLBT_EVI_SCR /* 신뢰성증거자료점수 */, nvl(A.RLBT_IBQ_SCR, 0) AS RLBT_IBQ_SCR /* 신뢰성탐문정보점수 */, nvl(A.UTLZ_INS_SCR, 0) + nvl(A.UTLZ_SPCF_SCR, 0) AS UTLZ_WHOL_SCR , nvl(A.UTLZ_INS_SCR, 0) AS UTLZ_INS_SCR /* 활용가능성감찰정보점수 */, nvl(A.UTLZ_SPCF_SCR, 0) AS UTLZ_SPCF_SCR /* 활용가능성특정정보점수 */, DECODE(A.RLS_YN,'1','공개','2','비공개') AS OPEN_NM FROM TBCSPBII100M A WHERE A.NTFCTN_RCPT_YMD BETWEEN #strStrAcpDt# AND #strEndAcpDt# AND A.OVRL_PIC_ENO = #OVRL_PIC_ENO# AND A.ADT_INFO_PRGRS_STTS_SECD IN ('10','13') AND A.ADT_INFO_PRGRS_STTS_SECD = '16' AND A.ADT_INFO_PRGRS_STTS_SECD = '18' ORDER BY CASE WHEN CSIT_TSC >0 THEN 1 WHEN RLBT_WHOL_SCR >0 THEN 1 WHEN UTLZ_WHOL_SCR >0 THEN 1 END ,A.INFO_YR ,A.FRS_MDM_SQNO ) T1