/* csp.bii.service.impl.CSPBII191QServiceImpl.CSPBII191QAudInfYeSelect */ SELECT (SELECT COUNT(*)
FROM TB_CSPBII100M WHERE TRNF_YMD IS NOT NULL AND TRNF_YMD = '10' /* 감사정보진행상태코드가 [10:제출접수]이상인 건 */ AND (CMPTN_YMD IS NULL OR CMPTN_YMD = '' OR ADT_INFO_PRGRS_STTS_SECD <> '99' /* 감사정보진행상태코드가 [99:완료]가 아닌 건 */ )) AS WRK_CNT1 , (SELECT COUNT(*)
FROM TB_CSPBII100M WHERE SBMSN_YMD >= #strStrDt# AND RTSO_YMD >= #strStrDt# AND RTSO_YMD = '10' /* 감사정보진행상태코드가 [10:제출접수]이상인 건 */ AND INFO_SECD = '1' /* 정보구분코드가 [1:감사대상정보]인 건 */ AND PRCS_DRCTN_SECD NOT IN ('3', '4', '7') /* 처리지시코드가 [3:감사대상, 4:이첩대상, 7:폐기]가 아닌 건 */ ) AS WRK_CNT2 , (SELECT COUNT(*)
FROM TB_CSPBII100M WHERE SBMSN_YMD >= #strStrDt# AND RTSO_YMD >= #strStrDt# AND RTSO_YMD = '10' /* 감사정보진행상태코드가 [10:제출접수]이상인 건 */ AND INFO_SECD IN ('2', '3') /* 정보구분코드가 [2:동향정보, 3:전산자료]인 건 */ AND PRCS_DRCTN_SECD NOT IN ('5', '7') /* 처리지시코드가 [5:참고자료, 7:폐기]가 아닌 건 */ ) AS WRK_CNT3 , (SELECT COUNT(*)
FROM TB_CSPBII100M WHERE TRNF_YMD IS NOT NULL AND CMPTN_YMD IS NOT NULL AND CMPTN_YMD >= #strStrDt# AND CMPTN_YMD = '10' /* 감사정보진행상태코드가 [10:제출접수]이상인 건 */ AND PRCS_RSLT_SECD IS NOT NULL AND PRCS_RSLT_SECD = '31' /* 처리결과코드가 [31:입건]인 건 */ AND (INFO_SECD <> '1' /* 정보구분코드가 [1:감사대상정보]가 아닌 건 */ OR PRCS_DRCTN_SECD <> '3' /* 처리지시코드가 [3:감사대상]이 아닌 건 */ )) AS WRK_CNT4 , (SELECT COUNT(*)
FROM TB_CSPBII100M WHERE TRNF_YMD IS NOT NULL AND CMPTN_YMD IS NOT NULL AND CMPTN_YMD >= #strStrDt# AND CMPTN_YMD = '10' /* 감사정보진행상태코드가 [10:제출접수]이상인 건 */ AND PRCS_RSLT_SECD IS NOT NULL AND PRCS_RSLT_SECD = '34' /* 처리결과코드가 [34:이첩입건]인 건 */ AND (INFO_SECD <> '1' /* 정보구분코드가 [1:감사대상정보]가 아닌 건 */ OR PRCS_DRCTN_SECD <> '4' /* 처리지시코드가 [4:이첩대상]이 아닌 건 */ )) AS WRK_CNT5 , (SELECT COUNT(*)
FROM TB_CSPBII100M WHERE TRNF_YMD IS NOT NULL AND CMPTN_YMD IS NOT NULL AND CMPTN_YMD >= #strStrDt# AND CMPTN_YMD = '10' /* 감사정보진행상태코드가 [10:제출접수]이상인 건 */ AND PRCS_RSLT_SECD IS NOT NULL AND PRCS_RSLT_SECD NOT IN ('31', '34', '35', '36', '37', '88') /* 처리결과코드가 [31:입건, 34:이첩입건, 35:감사활용, 36:불문, 37:낭비, 88:종결]이 아닌 건 */ ) AS WRK_CNT6 FROM DUAL