SELECT TO_CHAR(TO_DATE(A.SBMSN_YMD, 'YYYYMMDD'), 'YYYY.MM.DD') AS SBMSN_YMD /* 제출일자 */,
    (SELECT C.PROF_RBPRSN_INST_NM AS PROF_RBPRSN_INST_NM /* 증명책임자기관명 */
FROM TBDCMACM015M C WHERE C.INST_CD = A.REL_INST_CD) AS REL_ORG_NM , F_CODE_NM('1000042', A.PRCS_RSLT_SECD) AS HNDL_RSLT_NM , A.INFO_NM , B.ADT_INFO_CN , A.INFO_YR , A.INFO_SQNO FROM TB_CSPBII100M A , TB_CSPBII101L B WHERE A.INFO_YR = B.INFO_YR AND A.INFO_SQNO = B.INFO_SQNO AND (A.INFO_YR, A.INFO_SQNO) IN (SELECT C.INFO_YR /* 정보년도 */,
    C.INFO_SQNO /* 정보순번 */
FROM TB_CSPBII130L C WHERE C.CRU_RRNO_SCRTY = #CRU_RRNO_SCRTY#)