SELECT '(' || SUBSTR(A.ACQ_DT, 1, 4) || CASE WHEN LENGTH(A.ACQ_DT) > 4 THEN '.' || SUBSTR(A.ACQ_DT, 5, 2) ELSE '' END || CASE WHEN LENGTH(A.ACQ_DT) > 6 THEN '.' || SUBSTR(A.ACQ_DT, 7, 2) ELSE '' END || ')' AS SUBSTR /* 취득일 */,
    A.QLF_NM /* 자격명 */,
    CASE WHEN A.ENF_ORG_NM IS NOT NULL THEN '[시행청:' || A.ENF_ORG_NM || ']' END AS ENF_ORG_NM /* 시행기관명 */
FROM TBCSPBII330L A WHERE A.CRU_RRNO_SCRTY = #CRU_RRNO_SCRTY# ORDER BY A.ACQ_DT