SELECT '(' || SUBSTR(A.WRK_STR_DT, 1, 4) || CASE WHEN LENGTH(A.WRK_STR_DT) > 4 THEN '.' || SUBSTR(A.WRK_STR_DT, 5, 2) ELSE '' END || CASE WHEN LENGTH(A.WRK_STR_DT) > 6 THEN '.' || SUBSTR(A.WRK_STR_DT, 7, 2) ELSE '' END || '~' || SUBSTR(A.WRK_END_DT, 1, 4) || CASE WHEN LENGTH(A.WRK_END_DT) > 4 THEN '.' || SUBSTR(A.WRK_END_DT, 5, 2) ELSE '' END || CASE WHEN LENGTH(A.WRK_END_DT) > 6 THEN '.' || SUBSTR(A.WRK_END_DT, 7, 2) ELSE '' END || ')' AS SUBSTR /* 기간 */,
    A.SLCT_OOFC_NM /* 청탁근무처명 */,
    CASE WHEN JBPS_NM IS NOT NULL THEN '(' || A.JBPS_NM || ')' END AS JBPS_NM /* 직위명 */,
    CASE WHEN CHR_OW_NM IS NOT NULL THEN '[담당사무:' || A.CHR_OW_NM || ']' END AS CHR_OW_NM /* 담당사무명 */
FROM TBCSPBII342L A WHERE A.CRU_RRNO_SCRTY = #CRU_RRNO_SCRTY# ORDER BY A.WRK_STR_DT , A.WRK_END_DT