SELECT TO_CHAR(A.LAST_MDFCN_DT, 'YYYY.MM.DD') AS SLCT_CRTR_DT /* 청탁기준일시 */,
    (SELECT B.PROF_RBPRSN_INST_NM AS PROF_RBPRSN_INST_NM /* 증명책임자기관명 */
FROM TBDCMACM015M B WHERE B.INST_CD = A.OGDP_INST_CD) AS TPOF_OGDP_INST_NM /* 소속기관명 */ , A.WRK_RANK_CD AS JBGD_SECD /* 직급코드 */ , F_CODE_NM('1000058', A.WRK_RANK_CD) AS JBGD_NM /* 직급명 */ , TO_CHAR(TO_DATE(A.JOCP_DT, 'YYYYMMDD'), 'YYYY.MM.DD') AS JOCP_DT /* 입사일 */ , TO_CHAR(TO_DATE(A.RTRM_YMD, 'YYYYMMDD'), 'YYYY.MM.DD') AS RTRM_YMD /* 퇴사일 */ FROM TBCSPBII610L A WHERE A.CRU_RRNO_SCRTY = #CRU_RRNO_SCRTY# ORDER BY A.JOCP_DT , A.RTRM_YMD