SELECT (SELECT B.USER_NM AS USER_NM /* 사용자명 */
FROM TBDCMACM001M B WHERE B.TRGT_NOP_ENO = A.TRGT_NOP_ENO) AS SRCH_USR , A.TRGT_NOP_ENO , TO_CHAR(A.FRST_REG_DT, 'YYYY.MM.DD HH24:MI:SS') AS INQ_YMD FROM TBCSPBII402L A /* 대인정보기본 */ WHERE A.CRU_RRNO_SCRTY = #CRU_RRNO_SCRTY# ORDER BY TO_CHAR(A.FRST_REG_DT, 'YYYY.MM.DD HH24:MI:SS') DESC