SELECT A.INFO_RLPR_NM /* 정보관계자명 */,
    A.CRU_RRNO_SCRTY /* 부패주민등록번호보안 */,
    A.OGDP_INST_CD /* 소속기관코드 */,
    (SELECT B.PROF_RBPRSN_INST_NM AS PROF_RBPRSN_INST_NM /* 증명책임자기관명 */
FROM TBDCMACM015M B WHERE B.INST_CD = A.OGDP_INST_CD) AS TPOF_OGDP_INST_NM /* 소속기관명 */ , A.JBPS_NM /* 직위명 */ , F_CODE_NM('1000058', A.CRSP_OD_RANK_CD) || CASE WHEN A.JBGD_NM IS NOT NULL THEN ', ' || JBGD_NM ELSE '' END || CASE WHEN A.DTY_RK_CD IS NOT NULL THEN '(' || F_CODE_NM('1000174', A.DTY_RK_CD) || ')' ELSE '' END AS JBGD_NM /* 직급명 */ , A.JBPS_NM || CASE WHEN A.CRSP_OD_RANK_CD IS NOT NULL THEN ' / ' || F_CODE_NM('1000058', A.CRSP_OD_RANK_CD) ELSE '' END || CASE WHEN A.JBGD_NM IS NOT NULL THEN ', ' || JBGD_NM ELSE '' END || CASE WHEN A.DTY_RK_CD IS NOT NULL THEN '(' || F_CODE_NM('1000174', A.DTY_RK_CD) || ')' ELSE '' END AS PST_RANK_NM /* 직위/직급명 */ , CASE WHEN RSG_YN <> 'Y' AND A.EPS_MNG_DVSN_CD = '1' THEN '중점' ELSE '' END AS SE_NM /* 구분명 */ FROM TBCSPBII300M A /* 대인정보기본 */ WHERE 1 = 1 AND A.INFO_RLPR_NM LIKE #strNam# || '%'