SELECT TO_CHAR(TO_DATE(B.ENFC_YMD, 'YYYYMMDD'), 'YYYY.MM.DD') AS ENFC_YMD /* 시행일자 */,
    (SELECT E.PROF_RBPRSN_INST_NM AS PROF_RBPRSN_INST_NM /* 증명책임자기관명 */
FROM TBDCMACM015M E WHERE E.INST_CD = B.REL_INST_CD) AS REL_INST_NM , F_CODE_NM('1000002', A.DSPS_RQT_KDCD) AS DSPS_RQT_KND_CVN , A.DSPS_RQT_NM , C.SVW_CN , A.ADT_YR , A.ADT_NO , A.DSPS_RQT_SQNO FROM TB_BADEAR001M A , TB_BADEAR002D B , TB_BADEAR005M C WHERE A.ADT_YR = B.ADT_YR AND A.ADT_NO = B.ADT_NO AND A.DSPS_RQT_SQNO = B.DSPS_RQT_SQNO AND A.ADT_YR = C.ADT_YR AND A.ADT_NO = C.ADT_NO AND A.DSPS_RQT_SQNO = C.DSPS_RQT_SQNO AND (A.ADT_YR, A.ADT_NO, A.DSPS_RQT_SQNO) IN (SELECT D.ADT_YR /* 감사년도 */,
    D.ADT_NO /* 감사번호 */,
    D.DSPS_RQT_SQNO /* 처분요구순번 */
FROM TB_BADEAR007M D WHERE D.CRU_RRNO_SCRTY = #CRU_RRNO_SCRTY#)