SELECT A.INFO_YR || '-' || LPAD(A.FRS_MDM_SQNO, 5, '0') AS INFO_YR /* 정보년도 */,
    A.NTFCTN_RCPT_YMD /* 통보접수일자 */,
    A.CRU_CLCT_PATH_NM /* 부패수집경로명 */,
    (SELECT B.PROF_RBPRSN_INST_NM AS PROF_RBPRSN_INST_NM /* 증명책임자기관명 */
FROM TBDCMACM015M B WHERE B.INST_CD = A.REL_INST_CD) AS REL_ORG_NM , A.RSCH_ASMT_TTL , A.INFO_YR , A.FRS_MDM_SQNO , (SELECT C.CD_NM
FROM TBDCMACM013C C WHERE C.CD = A.INSP_INFO_STAT_CD AND CMM_CD_DVSN_CD = '1000865') AS STAT_NM , (SELECT D.CNSTN_MBCMT_DEPT_NM /* 자문위원부서명 */
FROM TBDCMACM002M D WHERE D.SRECS_DEPT_CD = A.CHAR_DEPT_CD ) AS CNSTN_MBCMT_DEPT_NM , A.CHAR_DEPT_CD , (SELECT E.USER_NM /* 사용자명 */
FROM TBDCMACM001M E WHERE E.TRGT_NOP_ENO = A.CHAR_CNB ) AS USER_NM , (SELECT SUBSTR(XMLAGG(XMLELEMENT(X,',',RLPR_NM) ORDER BY FRS_MDM_SQNO).EXTRACT('//text()'),2) AS SUBSTR
FROM TBCSPBII401L WHERE INFO_YR = A.INFO_YR AND FRS_MDM_SQNO = A.FRS_MDM_SQNO GROUP BY INFO_YR, FRS_MDM_SQNO) AS REL_PEN_NM_LIST , (SELECT C.CD_NM
FROM TBDCMACM013C C WHERE C.CD = A.INF_TYP AND CMM_CD_DVSN_CD = '1000875') AS INF_TYP /*정보유형*/ , (SELECT C.CD_NM
FROM TBDCMACM013C C WHERE C.CD = A.HNDL_DIRC AND CMM_CD_DVSN_CD = '1000876') AS HNDL_DIRC /*처리방향*/ , (SELECT C.CD_NM
FROM TBDCMACM013C C WHERE C.CD = A.HNDL_RSLT AND CMM_CD_DVSN_CD = '1000877') AS HNDL_RSLT /*처리결과*/ , A.FRST_USER_ID /*등록자*/ FROM TBCSPBII400L A /* 기타감찰자료정보내역 */ WHERE 1=1 AND (A.CHAR_DEPT_CD IN (SELECT SRECS_DEPT_CD /* 심사재심의부서코드 */
FROM TABLE(F_AUTH_DEPT(#strCnb#, #strDptAuth#, '', #strDptCd#))) OR A.CHAR_DEPT_CD IS NULL) AND (A.CHAR_DEPT_CD = #strDptCd2# OR A.CHAR_DEPT_CD IS NULL) AND A.NTFCTN_RCPT_YMD >= #strStrAcpDt# AND A.NTFCTN_RCPT_YMD &lt;= #strEndAcpDt# AND A.REL_INST_CD = #REL_INST_CD# AND A.ETC_INSP_MTRL_CLC_DVSN_CD = #strEtcInspMtrlClcDvsnCd# AND A.CHAR_CNB = #strChrCnb# AND A.INSP_INFO_STAT_CD = #strEtcInspInfoStatCd# AND A.INF_TYP = #strInfTyp# AND A.HNDL_DIRC = #strHndlDirc# AND A.HNDL_RSLT = #strHndlRslt# AND (A.INFO_YR, A.FRS_MDM_SQNO) IN (SELECT B.INFO_YR /* 정보년도 */,
    B.FRS_MDM_SQNO /* 포렌식매체순번 */
FROM TBCSPBII400L B WHERE B.CRU_CLCT_PATH_NM LIKE '%' || #strSchTxt# || '%' UNION ALL SELECT B.INFO_YR /* 정보년도 */,
    B.FRS_MDM_SQNO /* 포렌식매체순번 */
FROM TBCSPBII400L B WHERE B.RSCH_ASMT_TTL LIKE '%' || #strSchTxt# || '%' UNION ALL SELECT B.INFO_YR /* 정보년도 */,
    B.FRS_MDM_SQNO /* 포렌식매체순번 */
FROM TBCSPBII401L B WHERE B.RLPR_NM LIKE #strSchTxt# || '%')