SELECT A.INFO_YR || '-' || LPAD(A.FRS_MDM_SQNO, 5, '0') AS INFO_YR /* 정보년도 */,
    A.NTFCTN_RCPT_YMD /* 통보접수일자 */,
    A.REL_INST_CD /* 관계기관코드 */,
    (SELECT B.PROF_RBPRSN_INST_NM AS PROF_RBPRSN_INST_NM /* 증명책임자기관명 */
FROM TBDCMACM015M B WHERE B.INST_CD = A.REL_INST_CD) AS REL_ORG_NM , A.ETC_INSP_MTRL_CLC_DVSN_CD , A.CRU_CLCT_PATH_NM , (SELECT B.USER_NM || '(' || B.TRGT_NOP_ENO || ')' AS USER_NM /* 사용자명 */
FROM TBDCMACM001M B WHERE B.USER_ID = A.FRST_USER_ID) AS REGI_USR , A.RSCH_ASMT_TTL , A.CRU_ACT_DATA_CN , A.INFO_YR , A.FRS_MDM_SQNO ,(SELECT CVLCPT_DOC_ID /* 민원문서아이디 */
FROM TBCSPBII403L Z WHERE Z.INFO_YR = A.INFO_YR AND Z.FRS_MDM_SQNO = A.FRS_MDM_SQNO) AS CVLCPT_DOC_ID ,(SELECT B.TRGT_NOP_ENO /* 대상인원직원전산번호 */
FROM TBDCMACM001M B WHERE B.USER_ID = A.FRST_USER_ID) AS RGTR_ENO , A.INSP_INFO_STAT_CD , A.CHAR_DEPT_CD , A.CHAR_CNB , A.HST_DT , A.HST_SMM_TXT , A.INF_TYP , A.HNDL_DIRC , A.HNDL_RSLT , A.FRST_USER_ID FROM TBCSPBII400L A /* 기타감찰자료정보내역 */ WHERE 1=1 AND A.INFO_YR = #INFO_YR# AND A.FRS_MDM_SQNO = #FRS_MDM_SQNO# AND (A.INFO_YR, A.FRS_MDM_SQNO) IN (SELECT REGEXP_SUBSTR(#strYeList#, '[^||]+', 1, LEVEL),
    REGEXP_SUBSTR(#strSrnoList#, '[^||]+', 1, LEVEL)
FROM DUAL CONNECT BY LEVEL &lt;= REGEXP_COUNT(#strYeList#, '[^|]+'))