SELECT A.CVLCPT_RCPT_YR || '-부방-' || LPAD(CVLCPT_DCLR_RCPT_NO, 3, '0') AS CVLCPT_RCPT_YR /* 민원접수년도 */,
    A.CRU_RCPT_DT /* 부패접수일시 */,
    A.CRU_CLCT_PATH_NM /* 부패수집경로명 */,
    (SELECT B.PROF_RBPRSN_INST_NM AS PROF_RBPRSN_INST_NM /* 증명책임자기관명 */
FROM TBDCMACM015M B WHERE B.INST_CD = A.CRU_REL_INST_CD) AS REL_ORG_NM , A.CRU_TTL , A.CVLCPT_RCPT_YR , A.CVLCPT_DCLR_SECD , A.CVLCPT_DCLR_RCPT_NO FROM TB_CSPDCP811T A /* 부패방지자료정보내역 */ WHERE 1=1 AND A.CRU_RCPT_DT >= #strStrAcpDt# AND A.CRU_RCPT_DT &lt;= #strEndAcpDt# AND A.CRU_REL_INST_CD = #REL_INST_CD# AND (A.CVLCPT_RCPT_YR, A.CRU_ACTL_RCPT_NO) IN (SELECT B.CRU_ACTL_RCPT_NO /* 부패실제접수번호 */,
    B.CVLCPT_DCLR_RCPT_NO /* 민원신고접수번호 */
FROM TB_CSPDCP811T B WHERE B.CRU_CLCT_PATH_NM LIKE '%' || #strSchTxt# || '%' UNION ALL SELECT B.CVLCPT_RCPT_YR /* 민원접수년도 */,
    B.CVLCPT_DCLR_RCPT_NO /* 민원신고접수번호 */
FROM TB_CSPDCP811T B WHERE B.RSCH_ASMT_TTL LIKE '%' || #strSchTxt# || '%' ) ORDER BY MTRL_NO DESC