SELECT A.CVLCPT_RCPT_YR || '-부방-' || LPAD(CVLCPT_DCLR_RCPT_NO, 3, '0') AS CVLCPT_RCPT_YR /* 민원접수년도 */,
    A.CRU_RCPT_DT /* 부패접수일시 */,
    A.CRU_REL_INST_CD /* 부패관계기관코드 */,
    (SELECT B.PROF_RBPRSN_INST_NM AS PROF_RBPRSN_INST_NM /* 증명책임자기관명 */
FROM TBDCMACM015M B WHERE B.INST_CD = A.CRU_REL_INST_CD) AS REL_ORG_NM , A.CRU_CLCT_PATH_NM , (SELECT B.USER_NM || '(' || B.TRGT_NOP_ENO || ')' AS USER_NM /* 사용자명 */
FROM TBDCMACM001M B WHERE B.USER_ID = A.FRST_USER_ID) AS REGI_USR , A.CRU_TTL , A.CRU_ACT_DATA_CN , A.CVLCPT_RCPT_YR , A.CVLCPT_DCLR_SECD , A.CVLCPT_DCLR_RCPT_NO ,(SELECT CVLCPT_DOC_ID /* 민원문서아이디 */
FROM TBCSPBII404L Z WHERE Z.CVLCPT_RCPT_YR = A.CVLCPT_RCPT_YR AND Z.CVLCPT_DCLR_RCPT_NO = A.CVLCPT_DCLR_RCPT_NO) AS CVLCPT_DOC_ID ,(SELECT B.TRGT_NOP_ENO /* 대상인원직원전산번호 */
FROM TBDCMACM001M B WHERE B.USER_ID = A.FRST_USER_ID) AS RGTR_ENO FROM TB_CSPDCP811T A /* 기타감찰자료정보내역 */ WHERE A.CVLCPT_RCPT_YR = #CVLCPT_RCPT_YR# AND A.CVLCPT_DCLR_RCPT_NO = #CVLCPT_DCLR_RCPT_NO# AND A.CVLCPT_DCLR_SECD = '1'