SELECT (SELECT B.INFO_RLPR_NM AS INFO_RLPR_NM /* 정보관계자명 */
FROM TBCSPBII300M B WHERE B.CRU_RRNO_SCRTY = A.CRU_RRNO_SCRTY) AS INFO_RLPR_NM /* 성명 */ , A.CRU_RRNO_SCRTY /* 암호화주민등록번호 */ , A.OGDP_INST_CD /* 소속기관코드 */ , (SELECT B.PROF_RBPRSN_INST_NM /* 증명책임자기관명 */
FROM TBDCMACM015M B WHERE B.INST_CD = A.OGDP_INST_CD) AS TPOF_OGDP_INST_NM /* 소속기관명 */ , A.JBPS_NM /* 직위명 */ , A.JBPS_NM || CASE WHEN B.CRSP_OD_RANK_CD IS NOT NULL THEN ' / ' || F_CODE_NM('1000058', B.CRSP_OD_RANK_CD) ELSE '' END || CASE WHEN B.JBGD_NM IS NOT NULL THEN ', ' || B.JBGD_NM ELSE '' END || CASE WHEN B.DTY_RK_CD IS NOT NULL THEN '(' || F_CODE_NM('1000174', B.DTY_RK_CD) || ')' ELSE '' END AS PST_RANK_NM /* 직위/직급명 */ , A.EVL_DT /* 평가일 */ , A.INFO_YR || '-' || LPAD(A.FRS_MDM_SQNO, 5, '0') AS MTRL_NO /* 자료번호 */ , A.EVL_MTRL_DVSN_CD /* 평가자료구분코드 */ , A.EVL_DPT_CD /* 평가부서코드 */ , (SELECT B.CNSTN_MBCMT_DEPT_NM /* 자문위원부서명 */
FROM TBDCMACM002M B WHERE B.SRECS_DEPT_CD = A.EVL_DPT_CD) AS EVL_DPT_NM /* 평가부서명 */ , A.DTY_CABT_EVL_TXT /* 직무역량평가내용 */ , A.INFO_YR /* 연도 */ , A.FRS_MDM_SQNO /* 일련번호 */ FROM TBCSPBII500L A /* 기타감찰자료정보내역 */ , TBCSPBII300M B /* 대인정보기본 */ WHERE A.CRU_RRNO_SCRTY = B.CRU_RRNO_SCRTY AND A.INFO_YR = #INFO_YR# AND A.FRS_MDM_SQNO = #FRS_MDM_SQNO# AND (A.INFO_YR, A.FRS_MDM_SQNO) IN (SELECT REGEXP_SUBSTR(#strYeList#, '[^||]+', 1, LEVEL),
    REGEXP_SUBSTR(#strSrnoList#, '[^||]+', 1, LEVEL)
FROM DUAL CONNECT BY LEVEL &lt;= REGEXP_COUNT(#strYeList#, '[^|]+'))