SELECT MAX(B.INFO_RLPR_NM) AS INFO_RLPR_NM /* 정보관계자명 */,
    SUBSTR(A.CRU_RRNO_SCRTY, 1, 6) || '-******' AS SUBSTR,
    MAX((SELECT C.PROF_RBPRSN_INST_NM AS PROF_RBPRSN_INST_NM /* 증명책임자기관명 */
FROM TBDCMACM015M C WHERE C.INST_CD = B.OGDP_INST_CD)) AS TPOF_OGDP_INST_NM , MAX(B.JBPS_NM) AS JBPS_NM , MAX((SELECT D.CNSTN_MBCMT_DEPT_NM /* 자문위원부서명 */
FROM TBDCMACM015M C , TBDCMACM002M D WHERE C.ENFM_DPT_CD = D.SRECS_DEPT_CD AND C.INST_CD = B.OGDP_INST_CD)) AS TKCG_DEPT_NM , '평가(' || COUNT(*) || ')' AS MTRL_CNT , A.CRU_RRNO_SCRTY FROM TBCSPBII500L A /* 직무역량평가내역 */ , TBCSPBII300M B /* 대인정보기본 */ WHERE A.CRU_RRNO_SCRTY = B.CRU_RRNO_SCRTY AND B.INFO_RLPR_NM LIKE #strNam# || '%' AND A.CRU_RRNO_SCRTY LIKE #strRrn# || '%' AND B.OGDP_INST_CD = #OGDP_INST_CD# AND B.OGDP_INST_CD IN (SELECT C.INST_CD /* 기관코드 */
FROM TBDCMACM015M C WHERE C.ENFM_DPT_CD = #strChgDptCd#) GROUP BY A.CRU_RRNO_SCRTY ORDER BY MAX(B.INFO_RLPR_NM)