MERGE INTO TB_CSPBII172L A USING ( SELECT #YE# AS INFO_YR /* 정보년도 */,
    #SRNO# AS FRS_MDM_SQNO /* 포렌식매체순번 */,
    'SP-II-001' AS ADT_PLAN_DOC_TPCD /* 감사계획문서유형코드 */
FROM DUAL ) B ON ( A.INFO_YR = B.INFO_YR AND A.INFO_SQNO = B.FRS_MDM_SQNO AND A.INFO_DOC_TPCD = B.ADT_PLAN_DOC_TPCD ) WHEN MATCHED THEN UPDATE SET CVLCPT_DOC_ID = #CVLCPT_DOC_ID# /* 민원문서아이디 */, LAST_USER_ID = #LAST_USER_ID# /* 최종사용자아이디 */, LAST_MDFCN_DT = SYSDATE WHEN NOT MATCHED THEN INSERT INSERT ( INFO_YR /* 연도 */ , FRS_MDM_SQNO /* 일련번호 */ , ADT_PLAN_DOC_TPCD /* 문서유형코드 */ , CVLCPT_DOC_ID /* 문서ID */ , FRST_USER_ID /* 최초사용자ID */ , LAST_USER_ID /* 최종사용자ID */ , LAST_PRCS_DEPT_CD /* 최종거래부서코드 */ , LAST_MENU_ID /* 최종메뉴ID */ , FRST_REG_DT /* 최초등록일시 */ , LAST_MDFCN_DT /* 최종수정일시 */ ) VALUES ( #YE# , #SRNO# , 'SP-II-001' , #CVLCPT_DOC_ID# , #FRT_USR_ID# , #LAST_USER_ID# , #FNL_DEAL_DPT_CD# , #FNL_MNU_ID# , SYSDATE , SYSDATE )