SELECT T1.INFO_YR||'-'||T1.FRS_MDM_SQNO AS INFO_YR /* 정보년도 */,
    F_MNG_KND_AUDYRNO(T1.CVLCPT_ADT_YR, T1.ADT_NO, '', '-') ||'-'|| T2.DSPS_RQT_SQNO AS F_MNG_KND_AUDYRNO /* 처분번호 */,
    (Select AS ABR_DPT_NM_1
from TBDCMACM002M Where SRECS_DEPT_CD = T1.OGDP_DEPT_CD) AS COL3 /*감사소속*/ ,(Select ABR_DPT_NM_1
from TBDCMACM002M Where SRECS_DEPT_CD = T4.EXTT_OGDP_SECD) AS COL4 /*적출소속*/ ,(Select ABR_DPT_NM_1
from TBDCMACM002M Where SRECS_DEPT_CD = T5.SRECS_DEPT_CD) AS COL5 /*현재소속*/ ,T5.JBGD_SORT_SEQ AS COL6 /*직급*/ ,T5.TRGT_NOP_ENO AS COL7 /*전산번호*/ ,T5.USER_NM AS COL8 /*성명*/ ,substr(T3.ENFC_YMD,1,4)||'-'||substr(T3.ENFC_YMD,5,2)||'-'||substr(T3.ENFC_YMD,7,2) AS COL9 /*시행일자*/ ,'' AS COL10 /*제출부서*/ ,'' AS COL11 /*시행부서*/ ,'' AS COL12 /*처분종류*/ ,'' AS COL13 /*인원*/ ,'' AS COL14 /*금액*/ ,'' AS COL15 /*처분요구제목*/ , (Select USER_NM /* 사용자명 */
from TBDCMACM001M Where TRGT_NOP_ENO = T6.PRSNR_ENO) AS COL16 /*제출자*/ , '' AS COL17 /*제출소속*/ ,'' AS COL18 /*현재소속*/ , (Select CD_NM
from TBDCMACM013C Where CD = T4.EXTT_JBGD_SECD and CMM_CD_DVSN_CD = '1000173') AS COL19 /*직급*/ , T4.EXTT_NM AS COL20 /*적출자*/, T4.CTB_RT||'%' AS COL21 /*비율*/ FROM ( Select AA.INFO_YR /* 정보년도 */,
    AA.INFO_SQNO /* 정보순번 */,
    BB.ADT_YR /* 감사년도 */,
    BB.ADT_NO /* 감사번호 */,
    GG.BZTRVR_ENO /* 출장자직원전산번호 */,
    Min(GG.OGDP_DEPT_CD) AS OGDP_DEPT_CD /* 소속부서코드 */
From TBCSPBII170M AA, TBCSPBII174L BB, TBBADDED002M FF, TBBADDED005L GG Where AA.CMPTN_YMD BETWEEN #strStrDt# AND #strEndDt# and AA.PRCS_RSLT_SECD in ('31','32','33') and AA.INFO_YR = BB.INFO_YR and AA.FRS_MDM_SQNO = BB.FRS_MDM_SQNO and BB.CVLCPT_ADT_YR = FF.REL_ADT_YR and BB.ADT_NO = FF.REL_ADT_NO and GG.BZTRP_YR = FF.BZTRP_YR and GG.BZTRP_SQNO = FF.BZTRP_SQNO Group by AA.INFO_YR,AA.FRS_MDM_SQNO,BB.CVLCPT_ADT_YR, BB.ADT_NO, GG.BZTRVR_ENO ) T1 , TB_BADEAR001M T2, TB_BADEAR002D T3, TB_BADEAR004M T4, TBDCMACM001M T5, TB_CSPBII170M T6 WHERE T1.CVLCPT_ADT_YR = T2.ADT_YR AND T1.ADT_NO = T2.ADT_NO AND T2.ADT_YR = T3.ADT_YR AND T2.ADT_NO = T3.ADT_NO AND T2.DSPS_RQT_SQNO = T3.DSPS_RQT_SQNO AND T2.ADT_YR = T4.ADT_YR AND T2.ADT_NO = T4.ADT_NO AND T2.DSPS_RQT_SQNO = T4.DSPS_RQT_SQNO AND T1.BZTRVR_ENO = T4.EXTT_ID AND T1.BZTRVR_ENO = T5.TRGT_NOP_ENO AND T1.INFO_YR = T6.INFO_YR AND T1.FRS_MDM_SQNO = T6.INFO_SQNO ORDER BY T1.INFO_YR, T1.FRS_MDM_SQNO