SELECT F_CODE_NM('1000401', A.MTRL_KND_CD) AS F_CODE_NM /* 자료종류명 */,
    CASE WHEN A.MTRL_KND_CD = '2' THEN A.INF_NO ELSE NULL END AS MTRL_KND_CD /* 정보번호 */,
    (SELECT AS PROF_RBPRSN_INST_NM /* 증명책임자기관명 */
FROM TBDCMACM015M B WHERE B.INST_CD = A.JRSD_MAO_SECD) AS COPT_GD_NM /* 소관부처명 */ , A.MTRL_NM /* 자료명 */ , A.ETRT_NOC /* 추출건수 */ , A.ETRT_STAD_DT /* 추출기준일 */ , A.ETRT_TG_PRD_TXT /* 추출대상기간내용 */ , A.INFO_YR /* 연도 */ , A.FRS_MDM_SQNO /* 일련번호 */ FROM TBCSPBII800L A WHERE 1=1 AND A.JRSD_MAO_SECD = #JRSD_MAO_SECD# AND A.ACQR_ORG_CD = #strAcqrOrgCd# AND (A.MTRL_NM LIKE '%' || #strSrchTxt# || '%' OR A.ETRT_CLU_TXT LIKE '%' || #strSrchTxt# || '%') ORDER BY DECODE(A.MTRL_KND_CD, '1', 1, 2) , A.INFO_YR , A.FRS_MDM_SQNO