SELECT TRGT_NOP_ENO /* 대상인원직원전산번호 */,
    DECODE(JBPS_CD||'_'||SRECS_DEPT_CD,'0010300_100000','감찰관','0035400_100100','감찰담당관',F_CODE_NM('1000173',JBGD_SECD) ||' '|| USER_NM) AS USER_NM /* 사용자명 */
FROM UBCREOWN.TBDCMACM001M WHERE 1=1 AND USR_DVSN_CD ='1' AND (SRECS_DEPT_CD = '100100' OR JBPS_CD||'_'||SRECS_DEPT_CD = '0010300_100000') ORDER BY OCPT_SECD,nvl(INSD_JBGD_SECD, '50') ASC ,JBGD_SORT_SEQ,NOW_JBGD_APNTMN_YMD,SRT_KY_NO,CRNT_DPT_APM_DT,CRU_RRNO_SCRTY