MERGE INTO TB_CSPCOR104M A USING ( SELECT #CORR_DVSN_CD# AS CVLCPT_SECD /* 민원구분코드 */,
    #ACP_YR# AS CVLCPT_RCPT_YR /* 민원접수년도 */,
    #CORR_ACP_NO# AS CVLCPT_RCPT_NO /* 민원접수번호 */
FROM DUAL ) B ON ( A.CVLCPT_SECD = B.CVLCPT_SECD AND A.CVLCPT_RCPT_YR = B.CVLCPT_RCPT_YR AND A.CVLCPT_RCPT_NO = B.CVLCPT_RCPT_NO ) WHEN MATCHED THEN UPDATE SET ETC_LBSVC_MNG_DEPT_CD = #ETC_LBSVC_MNG_DEPT_CD# /* 기타용역관리부서코드 */, PRCS_PIC_ID = #PRCS_PIC_ID# /* 처리담당자아이디 */, CRU_HNDT_DT = CASE WHEN CRU_HNDT_DT IS NULL THEN SYSDATE ELSE CRU_HNDT_DT END /* 부패배부일시 */, CRU_LAST_HNDT_DT = SYSDATE /* 부패최종배부일시 */, LAST_USER_ID = #LAST_USER_ID# /* 최종사용자아이디 */, LAST_PRCS_DEPT_CD = #LAST_PRCS_DEPT_CD# /* 최종처리부서코드 */, LAST_MENU_ID = #LAST_MENU_ID# /* 최종메뉴아이디 */, LAST_MDFCN_DT = SYSDATE WHEN NOT MATCHED THEN INSERT INSERT ( CVLCPT_SECD ,CVLCPT_RCPT_YR ,CVLCPT_RCPT_NO ,ETC_LBSVC_MNG_DEPT_CD ,PRCS_PIC_ID ,CRU_HNDT_DT ,CRU_LAST_HNDT_DT ,FRST_USER_ID ,LAST_USER_ID ,LAST_PRCS_DEPT_CD ,LAST_MENU_ID ,FRST_REG_DT ,LAST_MDFCN_DT ) VALUES ( #CORR_DVSN_CD# ,#ACP_YR# ,#CORR_ACP_NO# ,#ETC_LBSVC_MNG_DEPT_CD# ,#PRCS_PIC_ID# ,SYSDATE ,SYSDATE ,#LAST_USER_ID# ,#LAST_USER_ID# ,#LAST_PRCS_DEPT_CD# ,#LAST_MENU_ID# ,SYSDATE ,SYSDATE ) /* 최종수정일시 */