SELECT T1.CVLCPT_SECD /* 민원구분코드 */,
    T1.CVLCPT_RCPT_YR /* 민원접수년도 */,
    T1.CVLCPT_RCPT_NO /* 민원접수번호 */,
    DECODE(T1.CRU_ACTL_RCPT_NO,NULL,NULL,T1.CVLCPT_RCPT_YR||'-부감-'||T1.CRU_ACTL_RCPT_NO) AS NTFCTN_RCPT_SQNO /* 통보접수순번 */,
    T1.INFO_RLPR_NM /* 정보관계자명 */,
    CASE WHEN ( SELECT COUNT(*)
FROM TB_CSPCOR107L S1 WHERE T1.CVLCPT_SECD = S1.CVLCPT_SECD AND T1.CVLCPT_RCPT_YR = S1.CVLCPT_RCPT_YR AND T1.CVLCPT_RCPT_NO = S1.CVLCPT_RCPT_NO ) > 0 THEN T1.RSCH_ASMT_TTL||'(합건 : '|| ( SELECT AGGR_CONCAT(S2.CVLCPT_RCPT_YR||'-부감-'||S2.CRU_ACTL_RCPT_NO,',')||')' AS CVLCPT_RCPT_YR /* 민원접수년도 */
FROM TB_CSPCOR107L S1 ,TB_CSPCOR101M S2 WHERE T1.CVLCPT_SECD = S1.CVLCPT_SECD AND T1.CVLCPT_RCPT_YR = S1.CVLCPT_RCPT_YR AND T1.CVLCPT_RCPT_NO = S1.CVLCPT_RCPT_NO AND S1.CVLCPT_MCA_CRU_SECD = S2.CVLCPT_SECD AND S1.CVLCPT_MCA_RCPT_YR = S2.CVLCPT_RCPT_YR AND S1.CVLCPT_MCA_CRU_RCPT_NO = S2.CVLCPT_RCPT_NO ) ELSE T1.RSCH_ASMT_TTL END RSCH_ASMT_TTL ,TO_CHAR(T1.CRU_RCPT_DT,'YYYY-MM-DD HH24:MI') AS CRU_RCPT_DT ,T1.CRU_RCPT_PATH_SECD ,F_CODE_NM('1000885',T1.CRU_RCPT_PATH_SECD) AS ACP_RUT_NM ,T1.CRU_PRCS_STTS_SECD ,F_CODE_NM('1000886',T1.CRU_PRCS_STTS_SECD) AS PRCS_STTS_NM ,T1.CRU_ACTL_RCPT_NO ,T1.CRU_TELNO ,TO_CHAR(T2.CRU_HNDT_DT,'YYYY-MM-DD') AS CRU_HNDT_DT ,F_DPT_INFO(T2.CRU_PRCS_DEPT_CD,'1') AS PRCS_DEPT_NM ,T2.CRU_PRCS_DEPT_CD ,F_USR_INFO_BY_ID(T2.CRU_PRCS_PIC_ID,'2') AS SRECS_PRCS_PIC_NM ,T2.CRU_PRCS_PIC_ID ,T1.CRU_RRNO_SCRTY ,TO_CHAR(TO_DATE(T1.PRCS_TERM_YMD),'YYYY-MM-DD') AS PRCS_TERM_YMD ,T2.CRU_EXMN_DEPT_CD ,F_DPT_INFO(T2.CRU_EXMN_DEPT_CD,'1') AS INSPC_DPT_NM ,T2.CRU_EXMN_PIC_ID ,F_USR_INFO_BY_ID(T2.CRU_EXMN_PIC_ID,'2') AS INSPC_CHGR_NM ,TO_CHAR(T1.CRU_PRCS_DT,'YYYY-MM-DD') AS CRU_PRCS_DT FROM TB_CSPCOR101M T1 ,TB_CSPCOR104M T2 WHERE T1.CVLCPT_SECD = '1' AND T1.CVLCPT_SECD = T2.CVLCPT_SECD(+) AND T1.CVLCPT_RCPT_YR = T2.CVLCPT_RCPT_YR(+) AND T1.CVLCPT_RCPT_NO = T2.CVLCPT_RCPT_NO(+) AND T1.CRU_PRCS_STTS_SECD NOT IN ('10','20','25') AND (T1.CVLCPT_SECD, T1.CVLCPT_RCPT_YR, T1.CVLCPT_RCPT_NO) NOT IN ( SELECT S1.CVLCPT_MCA_CRU_SECD /* 민원합건부패구분코드 */,
    S1.CVLCPT_MCA_RCPT_YR /* 민원합건접수년도 */,
    S1.CVLCPT_MCA_CRU_RCPT_NO /* 민원합건부패접수번호 */
FROM TB_CSPCOR107L S1 ) AND T1.CVLCPT_RCPT_YR = #CVLCPT_RCPT_YR# AND T1.CRU_ACTL_RCPT_NO = #CRU_ACTL_RCPT_NO# AND TO_CHAR(T1.CRU_RCPT_DT,'YYYYMMDD') &gt;= #strACP_DHS# AND TO_CHAR(T1.CRU_RCPT_DT,'YYYYMMDD') &lt;= #strACP_DHE# AND T1.CRU_PRCS_STTS_SECD = #CRU_PRCS_STTS_SECD# AND T1.INFO_RLPR_NM LIKE '%'||#strNAM#||'%' AND T1.RSCH_ASMT_TTL LIKE '%'||#strTIT_NM#||'%' AND T1.CRU_HMPG_APLY_NO = #CRU_HMPG_APLY_NO# AND T2.CRU_PRCS_DEPT_CD = #ETC_LBSVC_MNG_DEPT_CD# AND T2.CRU_PRCS_PIC_ID = #PRCS_PIC_ID# AND T1.CRU_PRCS_STTS_SECD IN ('90','100') AND T1.CRU_PRCS_STTS_SECD NOT IN ('90','100') ORDER BY T1.CVLCPT_RCPT_YR DESC,TO_NUMBER(T1.CRU_ACTL_RCPT_NO) DESC