SELECT A.CLM_SECD /* 청구구분코드 */,
    A.CVLCPT_RCPT_YR /* 민원접수년도 */,
    A.CVLCPT_RCPT_RCPT /* 민원접수번호 */,
    A.CVLCPT_ADT_TRGT_INST_CD /* 민원감사대상기관코드 */,
    B.PROF_RBPRSN_INST_NM /* 증명책임자기관명 */,
    ( SELECT Z.USR_DFN_TXT_1
FROM TBDCMACM013C Z WHERE Z.CMM_CD_DVSN_CD ='1000128' AND Z.CD = B.SLCT_PBNST_SECD) AS SLCT_PBNST_SECD ,F_ORG_HRK_NM(B.SLCT_JRSD_SG_SECD) AS COPT_SG_NM ,( SELECT F_CODE_NM('1000447',Z.USR_DFN_TXT_1)
FROM TBDCMACM013C Z WHERE Z.CMM_CD_DVSN_CD ='1000128' AND Z.CD = B.SLCT_PBNST_SECD) AS PUB_DVSN_NM FROM TB_CSPDCP104L A ,TBDCMACM015M B ,TB_CSPDCP101M C WHERE 1=1 AND C.CVLCPT_CLM_SECD = #CLM_SECD# AND C.TPOF_RCPT_YR = #CVLCPT_RCPT_YR# AND C.CVLCPT_CLM_SECD = A.CVLCPT_CLM_SECD AND C.TPOF_RCPT_YR = A.TPOF_RCPT_YR AND C.TPOF_RCPT_NO = A.TPOF_RCPT_NO AND A.TPOF_ADT_TRGT_INST_CD = B.INST_CD AND C.TPOF_RCPT_NO = #CVLCPT_RCPT_RCPT# AND C.TPOF_ACTL_RCPT_NO = #TPOF_ACTL_RCPT_NO#