SELECT A.CVLCPT_CLM_SECD /* 민원청구구분코드 */,
    A.TPOF_RCPT_YR||'-'||F_CODE_NM('1000740',A.CVLCPT_CLM_SECD)||'-'||A.TPOF_ACTL_RCPT_NO AS NTFCTN_RCPT_SQNO /* 통보접수순번 */,
    TO_CHAR(A.TPOF_RCPT_DT,'YYYY-MM-DD') AS CRU_RCPT_DT /* 부패접수일시 */,
    DECODE(A.TPOF_CLMT_KDCD,'1',A.TPOF_CVLPR_NM,'2',A.TPOF_CVLPR_RPRSV_NM,'3',A.TPOF_CVLPR_RPRSV_NM,'4',A.TPOF_CVLPR_NM) AS TPOF_CVLPR_NM /* 제보민원인명 */,
    CASE WHEN A.TPOF_RCPT_DEPT_CD = '071100' THEN '감사원 종합상담실' ELSE F_DPT_INFO(A.TPOF_RCPT_DEPT_CD,'1')||' 종합상담실' END AS TPOF_RCPT_DEPT_CD /* 제보접수부서코드 */
FROM TB_CSPDCP101M A WHERE 1=1 AND A.CVLCPT_CLM_SECD = #CLM_SECD# AND A.TPOF_RCPT_YR = #CVLCPT_RCPT_YR# AND A.TPOF_RCPT_NO = #CVLCPT_RCPT_RCPT#